'use client'

/**
 * Activities CRUD Page
 * Generated by HERA Enterprise Generator
 * 
 * Module: CRM
 * Entity: ACTIVITY
 * Smart Code: HERA.CRM.ACTIVITY.ENTITY.TASK.V1
 * Description: Tasks, meetings, calls, and other activities
 */

import React, { useState, useCallback, useEffect } from 'react'
import { MobilePageLayout } from '@/components/mobile/MobilePageLayout'
import { MobileFilters, type FilterField } from '@/components/mobile/MobileFilters'
import { MobileDataTable, type TableColumn, type TableRecord } from '@/components/mobile/MobileDataTable'
import { MobileCard } from '@/components/mobile/MobileCard'
import { MobileChart } from '@/components/mobile/MobileCharts'
import { useHERAAuth } from '@/components/auth/HERAAuthProvider'
import { useUniversalEntity } from '@/hooks/useUniversalEntity'
import { 
  Calendar,
  TrendingUp,
  Plus,
  Edit,
  Trash2,
  X,
  Save,
  Eye,
  Download,
  Upload,
  Search,
  Filter,
  MoreVertical,
  AlertCircle,
  CheckCircle,
  Clock
} from 'lucide-react'

/**
 * Activity Entity Interface
 * Extends TableRecord for HERA compliance
 */
interface Activity extends TableRecord {
  id: string
  entity_id?: string
  entity_name: string
  smart_code: string
  status?: string
  
  // Dynamic fields (stored in core_dynamic_data)
  subject?: string
  type?: string
  related_to?: string
  due_date?: string
  owner?: string
  priority?: string
  status?: string
  
  // System fields
  created_at?: string
  updated_at?: string
  created_by?: string
  updated_by?: string
  
  // Business rule fields
  
  workflow_stage?: string
}

/**
 * HERA Activity Smart Codes
 * Auto-generated from preset configuration
 */
const ACTIVITY_SMART_CODES = {
  ENTITY: 'HERA.CRM.ACTIVITY.ENTITY.TASK.V1',
  FIELD_SUBJECT: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.SUBJECT.V1',
  FIELD_TYPE: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.TYPE.V1',
  FIELD_RELATED_TO: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.RELATED_TO.V1',
  FIELD_DUE_DATE: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.DUE_DATE.V1',
  FIELD_OWNER: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.OWNER.V1',
  FIELD_PRIORITY: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.PRIORITY.V1',
  FIELD_STATUS: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.STATUS.V1',
  
  // Event smart codes for audit trail
  EVENT_CREATED: 'HERA.CRM.ACTIVITY.EVENT.TASK.V1.CREATED.V1',
  EVENT_UPDATED: 'HERA.CRM.ACTIVITY.EVENT.TASK.V1.UPDATED.V1',
  EVENT_DELETED: 'HERA.CRM.ACTIVITY.EVENT.TASK.V1.DELETED.V1',
  EVENT_STATUS_CHANGED: 'HERA.CRM.ACTIVITY.EVENT.TASK.V1.STATUS_CHANGED.V1'
} as const

/**
 * Activities Main Page Component
 * Enterprise-grade CRUD with quality gates and business rules
 */
export default function ActivitiesPage() {
  const { currentOrganization, isAuthenticated, user } = useHERAAuth()
  const [selectedActivities, setSelectedActivities] = useState<(string | number)[]>([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)
  const [showDeleteModal, setShowDeleteModal] = useState(false)
  const [currentActivity, setCurrentActivity] = useState<Activity | null>(null)
  const [activityToDelete, setActivityToDelete] = useState<Activity | null>(null)
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    workflow_stage: '',
    // Dynamic filter fields
    subject: '',
    type: '',
    related_to: ''
  })

  // HERA Universal Entity Integration
  const activityData = useUniversalEntity({
    entity_type: 'ACTIVITY',
    organizationId: currentOrganization?.id,
    filters: {
      include_dynamic: true,
      include_relationships: true,
      status: 'active'
    },
    dynamicFields: [
      { name: 'subject', type: 'text', smart_code: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.SUBJECT.V1', required: false },
      { name: 'type', type: 'text', smart_code: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.TYPE.V1', required: false },
      { name: 'related_to', type: 'text', smart_code: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.RELATED_TO.V1', required: false },
      { name: 'due_date', type: 'date', smart_code: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.DUE_DATE.V1', required: false },
      { name: 'owner', type: 'text', smart_code: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.OWNER.V1', required: true },
      { name: 'priority', type: 'text', smart_code: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.PRIORITY.V1', required: false },
      { name: 'status', type: 'text', smart_code: 'HERA.CRM.ACTIVITY.DYN.TASK.V1.STATUS.V1', required: false }
    ]
  })

  // Transform entities with business rule extensions
  const activitys: Activity[] = activityData.entities?.map((entity: any) => {
    return {
      id: entity.id,
      entity_id: entity.id,
      entity_name: entity.entity_name || '',
      smart_code: entity.smart_code || '',
      status: entity.status || 'active',
      
      // Map dynamic fields with type safety
      subject: entity.dynamic_data?.find((d: any) => d.field_name === 'subject')?.field_value_text || '',
      type: entity.dynamic_data?.find((d: any) => d.field_name === 'type')?.field_value_text || '',
      related_to: entity.dynamic_data?.find((d: any) => d.field_name === 'related_to')?.field_value_text || '',
      due_date: entity.dynamic_data?.find((d: any) => d.field_name === 'due_date')?.field_value_text || '',
      owner: entity.dynamic_data?.find((d: any) => d.field_name === 'owner')?.field_value_text || '',
      priority: entity.dynamic_data?.find((d: any) => d.field_name === 'priority')?.field_value_text || '',
      status: entity.dynamic_data?.find((d: any) => d.field_name === 'status')?.field_value_text || '',
      
      // System fields
      created_at: entity.created_at,
      updated_at: entity.updated_at,
      created_by: entity.created_by,
      updated_by: entity.updated_by
      
      ,
      // Workflow stage from relationships
      workflow_stage: entity.relationships?.find((r: any) => r.relationship_type === 'WORKFLOW_STAGE')?.target_entity?.entity_name || 'new'
    }
  }) || []

  // Enhanced KPI calculations with preset metrics
  const kpis = [
    {
      title: 'Total Activities',
      value: activitys.length.toString(),
      change: '+5.2%',
      trend: 'up' as const,
      icon: Calendar
    },
    {
      title: 'Active Activities',
      value: activitys.filter(item => item.status === 'active').length.toString(),
      change: '+2.1%',
      trend: 'up' as const,
      icon: CheckCircle
    },
    {
      title: 'This Month',
      value: activitys.filter(item => {
        if (!item.created_at) return false
        const created = new Date(item.created_at)
        const now = new Date()
        return created.getMonth() === now.getMonth() && created.getFullYear() === now.getFullYear()
      }).length.toString(),
      change: '+8.3%',
      trend: 'up' as const,
      icon: TrendingUp
    }
  ]

  // Enhanced table columns with business rule columns
  const columns: TableColumn[] = [
    { key: 'entity_name', label: 'Activity Name', sortable: true },
    { key: 'subject', label: 'Subject', sortable: true },
    { key: 'type', label: 'Type', sortable: true },
    { key: 'related_to', label: 'Related to', sortable: true },
    { key: 'due_date', label: 'Due date', sortable: true },
    { key: 'owner', label: 'Owner', sortable: true },
    { key: 'workflow_stage', label: 'Stage', sortable: true },
    { key: 'created_at', label: 'Created', sortable: true },
    { key: 'actions', label: 'Actions', sortable: false }
  ]

  // Enhanced filter fields with business rule filters
  const filterFields: FilterField[] = [
    { key: 'search', label: 'Search Activities', type: 'search' },
    { key: 'status', label: 'Status', type: 'select', options: [
      { value: '', label: 'All Status' },
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'Inactive' }
    ]},
    { key: 'workflow_stage', label: 'Workflow Stage', type: 'select', options: [
      { value: '', label: 'All Stages' },
      { value: 'new', label: 'New' },
      { value: 'in_progress', label: 'In Progress' },
      { value: 'completed', label: 'Completed' }
    ]},
    { key: 'subject', label: 'Subject', type: 'select', options: [
        { value: '', label: 'All Subjects' },
        ...Array.from(new Set(activitys.map(item => item.subject).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]},
    { key: 'type', label: 'Type', type: 'select', options: [
        { value: '', label: 'All Types' },
        ...Array.from(new Set(activitys.map(item => item.type).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]}
  ]

  // Enterprise CRUD Operations with Events
  const handleAddActivity = async (activityData: any) => {
    try {
      const result = await activityData.create({
        entity_type: 'ACTIVITY',
        entity_name: activityData.entity_name,
        smart_code: ACTIVITY_SMART_CODES.ENTITY,
        organization_id: currentOrganization?.id
      }, activityData)

      // Emit creation event for audit trail
      await activityData.emitEvent(ACTIVITY_SMART_CODES.EVENT_CREATED, {
        entity_id: result.id,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        data: activityData
      })

      setShowAddModal(false)
      console.log('✅ Activity created successfully')
    } catch (error) {
      console.error('❌ Error adding activity:', error)
    }
  }

  const handleEditActivity = async (activityData: any) => {
    if (!currentActivity) return
    
    try {
      await activityData.update(currentActivity.entity_id!, {
        entity_name: activityData.entity_name
      }, activityData)

      // Emit update event
      await activityData.emitEvent(ACTIVITY_SMART_CODES.EVENT_UPDATED, {
        entity_id: currentActivity.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        changes: activityData
      })

      setShowEditModal(false)
      setCurrentActivity(null)
      console.log('✅ Activity updated successfully')
    } catch (error) {
      console.error('❌ Error updating activity:', error)
    }
  }

  const handleDeleteActivity = async () => {
    if (!activityToDelete) return
    
    try {
      await activityData.delete(activityToDelete.entity_id!)

      // Emit deletion event
      await activityData.emitEvent(ACTIVITY_SMART_CODES.EVENT_DELETED, {
        entity_id: activityToDelete.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        entity_name: activityToDelete.entity_name
      })

      setShowDeleteModal(false)
      setActivityToDelete(null)
      console.log('✅ Activity deleted successfully')
    } catch (error) {
      console.error('❌ Error deleting activity:', error)
    }
  }

  
  const handleWorkflowChange = async (entityId: string, newStage: string) => {
    try {
      await activityData.emitEvent(ACTIVITY_SMART_CODES.EVENT_STATUS_CHANGED, {
        entity_id: entityId,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        old_stage: 'previous_stage', // Would get from current state
        new_stage: newStage
      })
      console.log('✅ Workflow stage changed successfully')
    } catch (error) {
      console.error('❌ Error changing workflow stage:', error)
    }
  }
  

  // Enterprise security checks
  if (!isAuthenticated) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
        <p>Please log in to access Activities.</p>
      </div>
    )
  }

  if (activityData.contextLoading) {
    return (
      <div className="p-4 text-center">
        <div className="animate-spin h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
        <p>Loading Activities...</p>
      </div>
    )
  }

  if (!currentOrganization) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-yellow-500 mx-auto mb-2" />
        <p>No organization context found. Please select an organization.</p>
      </div>
    )
  }

  return (
    <MobilePageLayout
      title="Activities"
      subtitle={`${activitys.length} total activitys`}
      primaryColor="#8764b8"
      accentColor="#5a4476"
      showBackButton={false}
    >
      {/* Enterprise KPI Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {kpis.map((kpi, index) => (
          <MobileCard key={index} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 font-medium">{kpi.title}</p>
                <p className="text-2xl font-bold" style={{ color: '#8764b8' }}>{kpi.value}</p>
                <p className={`text-xs font-medium ${kpi.trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                  {kpi.change}
                </p>
              </div>
              <kpi.icon className="h-8 w-8 text-gray-400" />
            </div>
          </MobileCard>
        ))}
      </div>

      {/* Enhanced Filters */}
      <MobileFilters
        fields={filterFields}
        values={filters}
        onChange={setFilters}
        className="mb-6"
      />

      {/* Enterprise Data Table */}
      <MobileDataTable
        data={activitys}
        columns={columns}
        selectedRows={selectedActivities}
        onRowSelect={setSelectedActivities}
        onRowClick={(activity) => {
          setCurrentActivity(activity)
          setShowEditModal(true)
        }}
        showBulkActions={selectedActivities.length > 0}
        bulkActions={[
          {
            label: 'Delete Selected',
            action: async () => {
              // Bulk delete with events
              for (const id of selectedActivities) {
                await activityData.delete(id.toString())
              }
              setSelectedActivities([])
            },
            variant: 'destructive'
          }
        ]}
        mobileCardRender={(activity) => (
          <MobileCard key={activity.id} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-lg">{activity.entity_name}</h3>
                <p className="text-sm text-gray-500">Stage: {activity.workflow_stage}</p>
                
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setCurrentActivity(activity)
                    setShowEditModal(true)
                  }}
                  className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                >
                  <Edit className="h-4 w-4" />
                </button>
                <button
                  onClick={() => {
                    setActivityToDelete(activity)
                    setShowDeleteModal(true)
                  }}
                  className="p-1 text-red-600 hover:bg-red-50 rounded"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            {/* Dynamic fields display */}
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Subject:</span>{' '}
              {activity.subject || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Type:</span>{' '}
              {activity.type || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Related To:</span>{' '}
              {activity.related_to || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Due Date:</span>{' '}
              {activity.due_date || 'N/A'}
            </div>
            
            <div className="text-xs text-gray-400 mt-2 pt-2 border-t">
              Created: {activity.created_at ? new Date(activity.created_at).toLocaleDateString() : 'N/A'}
            </div>
          </MobileCard>
        )}
      />

      {/* Floating Action Button */}
      <button
        onClick={() => setShowAddModal(true)}
        className="fixed bottom-6 right-6 text-white rounded-full p-4 shadow-lg transition-colors z-50 hover:shadow-xl"
        style={{ backgroundColor: '#8764b8' }}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Enterprise Modals */}
      {showAddModal && (
        <ActivityModal
          title="Add New Activity"
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          onSave={handleAddActivity}
          dynamicFields={activityData.dynamicFieldsConfig || []}
          businessRules={{"status_workflow":true,"audit_trail":true}}
        />
      )}

      {showEditModal && currentActivity && (
        <ActivityModal
          title="Edit Activity"
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false)
            setCurrentActivity(null)
          }}
          onSave={handleEditActivity}
          initialData={currentActivity}
          dynamicFields={activityData.dynamicFieldsConfig || []}
          businessRules={{"status_workflow":true,"audit_trail":true}}
        />
      )}

      {showDeleteModal && activityToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex items-center mb-4">
              <AlertCircle className="h-6 w-6 text-red-500 mr-2" />
              <h3 className="text-lg font-semibold">Delete Activity</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete "{activityToDelete.entity_name}"? This action cannot be undone.
            </p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => {
                  setShowDeleteModal(false)
                  setActivityToDelete(null)
                }}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteActivity}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </MobilePageLayout>
  )
}

/**
 * Enterprise Activity Modal Component
 * Enhanced with business rules and validation
 */
interface ActivityModalProps {
  title: string
  isOpen: boolean
  onClose: () => void
  onSave: (data: any) => void
  initialData?: Activity
  dynamicFields: any[]
  businessRules: any
}

function ActivityModal({ 
  title, 
  isOpen, 
  onClose, 
  onSave, 
  initialData, 
  dynamicFields,
  businessRules 
}: ActivityModalProps) {
  const [formData, setFormData] = useState(() => {
    const initial: any = { 
      entity_name: initialData?.entity_name || '' 
    }
    
    dynamicFields.forEach(field => {
      initial[field.name] = initialData?.[field.name as keyof Activity] || (field.type === 'number' ? 0 : '')
    })
    
    return initial
  })

  const [errors, setErrors] = useState<Record<string, string>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  const validateForm = () => {
    const newErrors: Record<string, string> = {}
    
    // Validate required fields
    dynamicFields.forEach(field => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label || field.name} is required`
      }
    })
    
    // Entity name validation
    if (!formData.entity_name?.trim()) {
      newErrors.entity_name = 'Activity name is required'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!validateForm()) return
    
    setIsSubmitting(true)
    try {
      await onSave(formData)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-xl font-semibold">{title}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Entity Name Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Activity Name *
              </label>
              <input
                type="text"
                value={formData.entity_name}
                onChange={(e) => setFormData({ ...formData, entity_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.entity_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.entity_name && (
                <p className="mt-1 text-sm text-red-600">{errors.entity_name}</p>
              )}
            </div>

            {/* Dynamic Fields with Enhanced Validation */}
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Subject 
              </label>
              <input
                type="text"
                value={formData.subject}
                onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.subject ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.subject && (
                <p className="mt-1 text-sm text-red-600">{errors.subject}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Type 
              </label>
              <input
                type="text"
                value={formData.type}
                onChange={(e) => setFormData({ ...formData, type: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.type ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.type && (
                <p className="mt-1 text-sm text-red-600">{errors.type}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Related To 
              </label>
              <input
                type="text"
                value={formData.related_to}
                onChange={(e) => setFormData({ ...formData, related_to: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.related_to ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.related_to && (
                <p className="mt-1 text-sm text-red-600">{errors.related_to}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Due Date 
              </label>
              <input
                type="date"
                value={formData.due_date}
                onChange={(e) => setFormData({ ...formData, due_date: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.due_date ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.due_date && (
                <p className="mt-1 text-sm text-red-600">{errors.due_date}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Owner *
              </label>
              <input
                type="text"
                value={formData.owner}
                onChange={(e) => setFormData({ ...formData, owner: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.owner ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.owner && (
                <p className="mt-1 text-sm text-red-600">{errors.owner}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Priority 
              </label>
              <input
                type="text"
                value={formData.priority}
                onChange={(e) => setFormData({ ...formData, priority: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.priority ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.priority && (
                <p className="mt-1 text-sm text-red-600">{errors.priority}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Status 
              </label>
              <input
                type="text"
                value={formData.status}
                onChange={(e) => setFormData({ ...formData, status: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.status ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.status && (
                <p className="mt-1 text-sm text-red-600">{errors.status}</p>
              )}
            </div>

            {/* Business Rules Info */}
            

            {/* Form Actions */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-white rounded-md hover:opacity-90 flex items-center disabled:opacity-50"
                style={{ backgroundColor: '#8764b8' }}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}