'use client'

/**
 * Purchase Orders CRUD Page
 * Generated by HERA Enterprise Generator
 * 
 * Module: PROCUREMENT
 * Entity: PURCHASE_ORDER
 * Smart Code: HERA.PURCHASE.TXN.PO.ENTITY.v1
 * Description: Purchase orders and procurement transactions
 */

import React, { useState, useCallback, useEffect } from 'react'
import { MobilePageLayout } from '@/components/mobile/MobilePageLayout'
import { MobileFilters, type FilterField } from '@/components/mobile/MobileFilters'
import { MobileDataTable, type TableColumn, type TableRecord } from '@/components/mobile/MobileDataTable'
import { MobileCard } from '@/components/mobile/MobileCard'
import { MobileChart } from '@/components/mobile/MobileCharts'
import { useHERAAuth } from '@/components/auth/HERAAuthProvider'
import { useUniversalEntity } from '@/hooks/useUniversalEntity'
import { 
  AlertCircle,
  CheckCircle,
  Clock,
  Download,
  Edit,
  Eye,
  Filter,
  MoreVertical,
  Plus,
  Save,
  Search,
  ShoppingCart,
  Trash2,
  TrendingUp,
  Upload,
  X
} from 'lucide-react'

/**
 * PurchaseOrder Entity Interface
 * Extends TableRecord for HERA compliance
 */
interface PurchaseOrder extends TableRecord {
  id: string
  entity_id?: string
  entity_name: string
  smart_code: string
  status?: string
  
  // Dynamic fields (stored in core_dynamic_data)
  po_number?: string
  vendor_id?: string
  order_date?: string
  delivery_date?: string
  total_amount?: string
  status?: string
  buyer?: string
  
  // System fields
  created_at?: string
  updated_at?: string
  created_by?: string
  updated_by?: string
  
  // Business rule fields
  
  
}

/**
 * HERA Purchase Order Smart Codes
 * Auto-generated from preset configuration
 */
const PURCHASE_ORDER_SMART_CODES = {
  ENTITY: 'HERA.PURCHASE.TXN.PO.ENTITY.v1',
  FIELD_PO_NUMBER: 'HERA.PURCHASE.TXN.PO.DYN.v1.PO_NUMBER.V1',
  FIELD_VENDOR_ID: 'HERA.PURCHASE.TXN.PO.DYN.v1.VENDOR_ID.V1',
  FIELD_ORDER_DATE: 'HERA.PURCHASE.TXN.PO.DYN.v1.ORDER_DATE.V1',
  FIELD_DELIVERY_DATE: 'HERA.PURCHASE.TXN.PO.DYN.v1.DELIVERY_DATE.V1',
  FIELD_TOTAL_AMOUNT: 'HERA.PURCHASE.TXN.PO.DYN.v1.TOTAL_AMOUNT.V1',
  FIELD_STATUS: 'HERA.PURCHASE.TXN.PO.DYN.v1.STATUS.V1',
  FIELD_BUYER: 'HERA.PURCHASE.TXN.PO.DYN.v1.BUYER.V1',
  
  // Event smart codes for audit trail
  EVENT_CREATED: 'HERA.PURCHASE.TXN.PO.EVENT.v1.CREATED.V1',
  EVENT_UPDATED: 'HERA.PURCHASE.TXN.PO.EVENT.v1.UPDATED.V1',
  EVENT_DELETED: 'HERA.PURCHASE.TXN.PO.EVENT.v1.DELETED.V1'
} as const

/**
 * Purchase Orders Main Page Component
 * Enterprise-grade CRUD with quality gates and business rules
 */
export default function PurchaseOrdersPage() {
  const { currentOrganization, isAuthenticated, user } = useHERAAuth()
  const [selectedPurchaseOrders, setSelectedPurchaseOrders] = useState<(string | number)[]>([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)
  const [showDeleteModal, setShowDeleteModal] = useState(false)
  const [currentPurchaseOrder, setCurrentPurchaseOrder] = useState<PurchaseOrder | null>(null)
  const [purchase_orderToDelete, setPurchaseOrderToDelete] = useState<PurchaseOrder | null>(null)
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    // Dynamic filter fields
    po_number: '',
    vendor_id: '',
    order_date: ''
  })

  // HERA Universal Entity Integration
  const purchase_orderData = useUniversalEntity({
    entity_type: 'PURCHASE_ORDER',
    organizationId: currentOrganization?.id,
    filters: {
      include_dynamic: true,
      include_relationships: undefined,
      status: 'active'
    },
    dynamicFields: [
      { name: 'po_number', type: 'text', smart_code: 'HERA.PURCHASE.TXN.PO.DYN.v1.PO_NUMBER.V1', required: false },
      { name: 'vendor_id', type: 'text', smart_code: 'HERA.PURCHASE.TXN.PO.DYN.v1.VENDOR_ID.V1', required: false },
      { name: 'order_date', type: 'text', smart_code: 'HERA.PURCHASE.TXN.PO.DYN.v1.ORDER_DATE.V1', required: false },
      { name: 'delivery_date', type: 'text', smart_code: 'HERA.PURCHASE.TXN.PO.DYN.v1.DELIVERY_DATE.V1', required: false },
      { name: 'total_amount', type: 'text', smart_code: 'HERA.PURCHASE.TXN.PO.DYN.v1.TOTAL_AMOUNT.V1', required: false },
      { name: 'status', type: 'text', smart_code: 'HERA.PURCHASE.TXN.PO.DYN.v1.STATUS.V1', required: false },
      { name: 'buyer', type: 'text', smart_code: 'HERA.PURCHASE.TXN.PO.DYN.v1.BUYER.V1', required: false }
    ]
  })

  // Transform entities with business rule extensions
  const purchase_orders: PurchaseOrder[] = purchase_orderData.entities?.map((entity: any) => {
    return {
      id: entity.id,
      entity_id: entity.id,
      entity_name: entity.entity_name || '',
      smart_code: entity.smart_code || '',
      status: entity.status || 'active',
      
      // Map dynamic fields with type safety
      po_number: entity.dynamic_data?.find((d: any) => d.field_name === 'po_number')?.field_value_text || '',
      vendor_id: entity.dynamic_data?.find((d: any) => d.field_name === 'vendor_id')?.field_value_text || '',
      order_date: entity.dynamic_data?.find((d: any) => d.field_name === 'order_date')?.field_value_text || '',
      delivery_date: entity.dynamic_data?.find((d: any) => d.field_name === 'delivery_date')?.field_value_text || '',
      total_amount: entity.dynamic_data?.find((d: any) => d.field_name === 'total_amount')?.field_value_text || '',
      status: entity.dynamic_data?.find((d: any) => d.field_name === 'status')?.field_value_text || '',
      buyer: entity.dynamic_data?.find((d: any) => d.field_name === 'buyer')?.field_value_text || '',
      
      // System fields
      created_at: entity.created_at,
      updated_at: entity.updated_at,
      created_by: entity.created_by,
      updated_by: entity.updated_by
      
      
    }
  }) || []

  // Enhanced KPI calculations with preset metrics
  const kpis = [
    {
      title: 'Total Purchase Orders',
      value: purchase_orders.length.toString(),
      change: '+5.2%',
      trend: 'up' as const,
      icon: ShoppingCart
    },
    {
      title: 'Active Purchase Orders',
      value: purchase_orders.filter(item => item.status === 'active').length.toString(),
      change: '+2.1%',
      trend: 'up' as const,
      icon: CheckCircle
    },
    {
      title: 'This Month',
      value: purchase_orders.filter(item => {
        if (!item.created_at) return false
        const created = new Date(item.created_at)
        const now = new Date()
        return created.getMonth() === now.getMonth() && created.getFullYear() === now.getFullYear()
      }).length.toString(),
      change: '+8.3%',
      trend: 'up' as const,
      icon: TrendingUp
    }
  ]

  // Enhanced table columns with business rule columns
  const columns: TableColumn[] = [
    { key: 'entity_name', label: 'Purchase Order Name', sortable: true },
    { key: 'po_number', label: 'Po number', sortable: true },
    { key: 'vendor_id', label: 'Vendor id', sortable: true },
    { key: 'order_date', label: 'Order date', sortable: true },
    { key: 'delivery_date', label: 'Delivery date', sortable: true },
    { key: 'total_amount', label: 'Total amount', sortable: true },
    { key: 'created_at', label: 'Created', sortable: true },
    { key: 'actions', label: 'Actions', sortable: false }
  ]

  // Enhanced filter fields with business rule filters
  const filterFields: FilterField[] = [
    { key: 'search', label: 'Search Purchase Orders', type: 'search' },
    { key: 'status', label: 'Status', type: 'select', options: [
      { value: '', label: 'All Status' },
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'Inactive' }
    ]},
    { key: 'po_number', label: 'Po Number', type: 'select', options: [
        { value: '', label: 'All Po Numbers' },
        ...Array.from(new Set(purchase_orders.map(item => item.po_number).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]},
    { key: 'vendor_id', label: 'Vendor Id', type: 'select', options: [
        { value: '', label: 'All Vendor Ids' },
        ...Array.from(new Set(purchase_orders.map(item => item.vendor_id).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]}
  ]

  // Enterprise CRUD Operations with Events
  const handleAddPurchaseOrder = async (purchase_orderData: any) => {
    try {
      const result = await purchase_orderData.create({
        entity_type: 'PURCHASE_ORDER',
        entity_name: purchase_orderData.entity_name,
        smart_code: PURCHASE_ORDER_SMART_CODES.ENTITY,
        organization_id: currentOrganization?.id
      }, purchase_orderData)

      // Emit creation event for audit trail
      await purchase_orderData.emitEvent(PURCHASE_ORDER_SMART_CODES.EVENT_CREATED, {
        entity_id: result.id,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        data: purchase_orderData
      })

      setShowAddModal(false)
      console.log('✅ Purchase Order created successfully')
    } catch (error) {
      console.error('❌ Error adding purchase_order:', error)
    }
  }

  const handleEditPurchaseOrder = async (purchase_orderData: any) => {
    if (!currentPurchaseOrder) return
    
    try {
      await purchase_orderData.update(currentPurchaseOrder.entity_id!, {
        entity_name: purchase_orderData.entity_name
      }, purchase_orderData)

      // Emit update event
      await purchase_orderData.emitEvent(PURCHASE_ORDER_SMART_CODES.EVENT_UPDATED, {
        entity_id: currentPurchaseOrder.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        changes: purchase_orderData
      })

      setShowEditModal(false)
      setCurrentPurchaseOrder(null)
      console.log('✅ Purchase Order updated successfully')
    } catch (error) {
      console.error('❌ Error updating purchase_order:', error)
    }
  }

  const handleDeletePurchaseOrder = async () => {
    if (!purchase_orderToDelete) return
    
    try {
      await purchase_orderData.delete(purchase_orderToDelete.entity_id!)

      // Emit deletion event
      await purchase_orderData.emitEvent(PURCHASE_ORDER_SMART_CODES.EVENT_DELETED, {
        entity_id: purchase_orderToDelete.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        entity_name: purchase_orderToDelete.entity_name
      })

      setShowDeleteModal(false)
      setPurchaseOrderToDelete(null)
      console.log('✅ Purchase Order deleted successfully')
    } catch (error) {
      console.error('❌ Error deleting purchase_order:', error)
    }
  }

  
  

  // Enterprise security checks
  if (!isAuthenticated) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
        <p>Please log in to access Purchase Orders.</p>
      </div>
    )
  }

  if (purchase_orderData.contextLoading) {
    return (
      <div className="p-4 text-center">
        <div className="animate-spin h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
        <p>Loading Purchase Orders...</p>
      </div>
    )
  }

  if (!currentOrganization) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-yellow-500 mx-auto mb-2" />
        <p>No organization context found. Please select an organization.</p>
      </div>
    )
  }

  return (
    <MobilePageLayout
      title="Purchase Orders"
      subtitle={`${purchase_orders.length} total purchase_orders`}
      primaryColor="#486581"
      accentColor="#324b63"
      showBackButton={false}
    >
      {/* Enterprise KPI Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {kpis.map((kpi, index) => (
          <MobileCard key={index} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 font-medium">{kpi.title}</p>
                <p className="text-2xl font-bold" style={{ color: '#486581' }}>{kpi.value}</p>
                <p className={`text-xs font-medium ${kpi.trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                  {kpi.change}
                </p>
              </div>
              <kpi.icon className="h-8 w-8 text-gray-400" />
            </div>
          </MobileCard>
        ))}
      </div>

      {/* Enhanced Filters */}
      <MobileFilters
        fields={filterFields}
        values={filters}
        onChange={setFilters}
        className="mb-6"
      />

      {/* Enterprise Data Table */}
      <MobileDataTable
        data={purchase_orders}
        columns={columns}
        selectedRows={selectedPurchaseOrders}
        onRowSelect={setSelectedPurchaseOrders}
        onRowClick={(purchase_order) => {
          setCurrentPurchaseOrder(purchase_order)
          setShowEditModal(true)
        }}
        showBulkActions={selectedPurchaseOrders.length > 0}
        bulkActions={[
          {
            label: 'Delete Selected',
            action: async () => {
              // Bulk delete with events
              for (const id of selectedPurchaseOrders) {
                await purchase_orderData.delete(id.toString())
              }
              setSelectedPurchaseOrders([])
            },
            variant: 'destructive'
          }
        ]}
        mobileCardRender={(purchase_order) => (
          <MobileCard key={purchase_order.id} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-lg">{purchase_order.entity_name}</h3>
                
                
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setCurrentPurchaseOrder(purchase_order)
                    setShowEditModal(true)
                  }}
                  className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                >
                  <Edit className="h-4 w-4" />
                </button>
                <button
                  onClick={() => {
                    setPurchaseOrderToDelete(purchase_order)
                    setShowDeleteModal(true)
                  }}
                  className="p-1 text-red-600 hover:bg-red-50 rounded"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            {/* Dynamic fields display */}
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Po Number:</span>{' '}
              {purchase_order.po_number || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Vendor Id:</span>{' '}
              {purchase_order.vendor_id || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Order Date:</span>{' '}
              {purchase_order.order_date || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Delivery Date:</span>{' '}
              {purchase_order.delivery_date || 'N/A'}
            </div>
            
            <div className="text-xs text-gray-400 mt-2 pt-2 border-t">
              Created: {purchase_order.created_at ? new Date(purchase_order.created_at).toLocaleDateString() : 'N/A'}
            </div>
          </MobileCard>
        )}
      />

      {/* Floating Action Button */}
      <button
        onClick={() => setShowAddModal(true)}
        className="fixed bottom-6 right-6 text-white rounded-full p-4 shadow-lg transition-colors z-50 hover:shadow-xl"
        style={{ backgroundColor: '#486581' }}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Enterprise Modals */}
      {showAddModal && (
        <PurchaseOrderModal
          title="Add New Purchase Order"
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          onSave={handleAddPurchaseOrder}
          dynamicFields={purchase_orderData.dynamicFieldsConfig || []}
          businessRules={{"sequential_numbering":true,"audit_trail":true,"approval_workflow":true}}
        />
      )}

      {showEditModal && currentPurchaseOrder && (
        <PurchaseOrderModal
          title="Edit Purchase Order"
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false)
            setCurrentPurchaseOrder(null)
          }}
          onSave={handleEditPurchaseOrder}
          initialData={currentPurchaseOrder}
          dynamicFields={purchase_orderData.dynamicFieldsConfig || []}
          businessRules={{"sequential_numbering":true,"audit_trail":true,"approval_workflow":true}}
        />
      )}

      {showDeleteModal && purchase_orderToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex items-center mb-4">
              <AlertCircle className="h-6 w-6 text-red-500 mr-2" />
              <h3 className="text-lg font-semibold">Delete Purchase Order</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete "{purchase_orderToDelete.entity_name}"? This action cannot be undone.
            </p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => {
                  setShowDeleteModal(false)
                  setPurchaseOrderToDelete(null)
                }}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleDeletePurchaseOrder}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </MobilePageLayout>
  )
}

/**
 * Enterprise Purchase Order Modal Component
 * Enhanced with business rules and validation
 */
interface PurchaseOrderModalProps {
  title: string
  isOpen: boolean
  onClose: () => void
  onSave: (data: any) => void
  initialData?: PurchaseOrder
  dynamicFields: any[]
  businessRules: any
}

function PurchaseOrderModal({ 
  title, 
  isOpen, 
  onClose, 
  onSave, 
  initialData, 
  dynamicFields,
  businessRules 
}: PurchaseOrderModalProps) {
  const [formData, setFormData] = useState(() => {
    const initial: any = { 
      entity_name: initialData?.entity_name || '' 
    }
    
    dynamicFields.forEach(field => {
      initial[field.name] = initialData?.[field.name as keyof PurchaseOrder] || (field.type === 'number' ? 0 : '')
    })
    
    return initial
  })

  const [errors, setErrors] = useState<Record<string, string>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  const validateForm = () => {
    const newErrors: Record<string, string> = {}
    
    // Validate required fields
    dynamicFields.forEach(field => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label || field.name} is required`
      }
    })
    
    // Entity name validation
    if (!formData.entity_name?.trim()) {
      newErrors.entity_name = 'Purchase Order name is required'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!validateForm()) return
    
    setIsSubmitting(true)
    try {
      await onSave(formData)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-xl font-semibold">{title}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Entity Name Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Purchase Order Name *
              </label>
              <input
                type="text"
                value={formData.entity_name}
                onChange={(e) => setFormData({ ...formData, entity_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.entity_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.entity_name && (
                <p className="mt-1 text-sm text-red-600">{errors.entity_name}</p>
              )}
            </div>

            {/* Dynamic Fields with Enhanced Validation */}
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Po Number 
              </label>
              <input
                type="text"
                value={formData.po_number}
                onChange={(e) => setFormData({ ...formData, po_number: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.po_number ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.po_number && (
                <p className="mt-1 text-sm text-red-600">{errors.po_number}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Vendor Id 
              </label>
              <input
                type="text"
                value={formData.vendor_id}
                onChange={(e) => setFormData({ ...formData, vendor_id: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.vendor_id ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.vendor_id && (
                <p className="mt-1 text-sm text-red-600">{errors.vendor_id}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Order Date 
              </label>
              <input
                type="text"
                value={formData.order_date}
                onChange={(e) => setFormData({ ...formData, order_date: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.order_date ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.order_date && (
                <p className="mt-1 text-sm text-red-600">{errors.order_date}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Delivery Date 
              </label>
              <input
                type="text"
                value={formData.delivery_date}
                onChange={(e) => setFormData({ ...formData, delivery_date: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.delivery_date ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.delivery_date && (
                <p className="mt-1 text-sm text-red-600">{errors.delivery_date}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Total Amount 
              </label>
              <input
                type="text"
                value={formData.total_amount}
                onChange={(e) => setFormData({ ...formData, total_amount: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.total_amount ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.total_amount && (
                <p className="mt-1 text-sm text-red-600">{errors.total_amount}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Status 
              </label>
              <input
                type="text"
                value={formData.status}
                onChange={(e) => setFormData({ ...formData, status: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.status ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.status && (
                <p className="mt-1 text-sm text-red-600">{errors.status}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Buyer 
              </label>
              <input
                type="text"
                value={formData.buyer}
                onChange={(e) => setFormData({ ...formData, buyer: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.buyer ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.buyer && (
                <p className="mt-1 text-sm text-red-600">{errors.buyer}</p>
              )}
            </div>

            {/* Business Rules Info */}
            

            {/* Form Actions */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-white rounded-md hover:opacity-90 flex items-center disabled:opacity-50"
                style={{ backgroundColor: '#486581' }}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}