'use client'

/**
 * Vendors CRUD Page
 * Generated by HERA Enterprise Generator
 * 
 * Module: PROCUREMENT
 * Entity: VENDOR
 * Smart Code: HERA.PURCHASE.MASTER.VENDOR.ENTITY.v1
 * Description: Supplier vendor master data
 */

import React, { useState, useCallback, useEffect } from 'react'
import { MobilePageLayout } from '@/components/mobile/MobilePageLayout'
import { MobileFilters, type FilterField } from '@/components/mobile/MobileFilters'
import { MobileDataTable, type TableColumn, type TableRecord } from '@/components/mobile/MobileDataTable'
import { MobileCard } from '@/components/mobile/MobileCard'
import { MobileChart } from '@/components/mobile/MobileCharts'
import { useHERAAuth } from '@/components/auth/HERAAuthProvider'
import { useUniversalEntity } from '@/hooks/useUniversalEntity'
import { 
  AlertCircle,
  Building2,
  CheckCircle,
  Clock,
  Download,
  Edit,
  Eye,
  Filter,
  MoreVertical,
  Plus,
  Save,
  Search,
  Trash2,
  TrendingUp,
  Upload,
  X
} from 'lucide-react'

/**
 * Vendor Entity Interface
 * Extends TableRecord for HERA compliance
 */
interface Vendor extends TableRecord {
  id: string
  entity_id?: string
  entity_name: string
  smart_code: string
  status?: string
  
  // Dynamic fields (stored in core_dynamic_data)
  vendor_code?: string
  tax_id?: string
  email?: string
  phone?: string
  payment_terms?: string
  credit_limit?: string
  category?: string
  location?: string
  
  // System fields
  created_at?: string
  updated_at?: string
  created_by?: string
  updated_by?: string
  
  // Business rule fields
  
  
}

/**
 * HERA Vendor Smart Codes
 * Auto-generated from preset configuration
 */
const VENDOR_SMART_CODES = {
  ENTITY: 'HERA.PURCHASE.MASTER.VENDOR.ENTITY.v1',
  FIELD_VENDOR_CODE: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.VENDOR_CODE.V1',
  FIELD_TAX_ID: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.TAX_ID.V1',
  FIELD_EMAIL: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.EMAIL.V1',
  FIELD_PHONE: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.PHONE.V1',
  FIELD_PAYMENT_TERMS: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.PAYMENT_TERMS.V1',
  FIELD_CREDIT_LIMIT: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.CREDIT_LIMIT.V1',
  FIELD_CATEGORY: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.CATEGORY.V1',
  FIELD_LOCATION: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.LOCATION.V1',
  
  // Event smart codes for audit trail
  EVENT_CREATED: 'HERA.PURCHASE.MASTER.VENDOR.EVENT.v1.CREATED.V1',
  EVENT_UPDATED: 'HERA.PURCHASE.MASTER.VENDOR.EVENT.v1.UPDATED.V1',
  EVENT_DELETED: 'HERA.PURCHASE.MASTER.VENDOR.EVENT.v1.DELETED.V1'
} as const

/**
 * Vendors Main Page Component
 * Enterprise-grade CRUD with quality gates and business rules
 */
export default function VendorsPage() {
  const { currentOrganization, isAuthenticated, user } = useHERAAuth()
  const [selectedVendors, setSelectedVendors] = useState<(string | number)[]>([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)
  const [showDeleteModal, setShowDeleteModal] = useState(false)
  const [currentVendor, setCurrentVendor] = useState<Vendor | null>(null)
  const [vendorToDelete, setVendorToDelete] = useState<Vendor | null>(null)
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    // Dynamic filter fields
    vendor_code: '',
    tax_id: '',
    email: ''
  })

  // HERA Universal Entity Integration
  const vendorData = useUniversalEntity({
    entity_type: 'VENDOR',
    organizationId: currentOrganization?.id,
    filters: {
      include_dynamic: true,
      include_relationships: undefined,
      status: 'active'
    },
    dynamicFields: [
      { name: 'vendor_code', type: 'text', smart_code: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.VENDOR_CODE.V1', required: false },
      { name: 'tax_id', type: 'text', smart_code: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.TAX_ID.V1', required: false },
      { name: 'email', type: 'email', smart_code: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.EMAIL.V1', required: true },
      { name: 'phone', type: 'phone', smart_code: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.PHONE.V1', required: true },
      { name: 'payment_terms', type: 'text', smart_code: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.PAYMENT_TERMS.V1', required: false },
      { name: 'credit_limit', type: 'text', smart_code: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.CREDIT_LIMIT.V1', required: false },
      { name: 'category', type: 'text', smart_code: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.CATEGORY.V1', required: false },
      { name: 'location', type: 'text', smart_code: 'HERA.PURCHASE.MASTER.VENDOR.DYN.v1.LOCATION.V1', required: false }
    ]
  })

  // Transform entities with business rule extensions
  const vendors: Vendor[] = vendorData.entities?.map((entity: any) => {
    return {
      id: entity.id,
      entity_id: entity.id,
      entity_name: entity.entity_name || '',
      smart_code: entity.smart_code || '',
      status: entity.status || 'active',
      
      // Map dynamic fields with type safety
      vendor_code: entity.dynamic_data?.find((d: any) => d.field_name === 'vendor_code')?.field_value_text || '',
      tax_id: entity.dynamic_data?.find((d: any) => d.field_name === 'tax_id')?.field_value_text || '',
      email: entity.dynamic_data?.find((d: any) => d.field_name === 'email')?.field_value_text || '',
      phone: entity.dynamic_data?.find((d: any) => d.field_name === 'phone')?.field_value_text || '',
      payment_terms: entity.dynamic_data?.find((d: any) => d.field_name === 'payment_terms')?.field_value_text || '',
      credit_limit: entity.dynamic_data?.find((d: any) => d.field_name === 'credit_limit')?.field_value_text || '',
      category: entity.dynamic_data?.find((d: any) => d.field_name === 'category')?.field_value_text || '',
      location: entity.dynamic_data?.find((d: any) => d.field_name === 'location')?.field_value_text || '',
      
      // System fields
      created_at: entity.created_at,
      updated_at: entity.updated_at,
      created_by: entity.created_by,
      updated_by: entity.updated_by
      
      
    }
  }) || []

  // Enhanced KPI calculations with preset metrics
  const kpis = [
    {
      title: 'Total Vendors',
      value: vendors.length.toString(),
      change: '+5.2%',
      trend: 'up' as const,
      icon: Building2
    },
    {
      title: 'Active Vendors',
      value: vendors.filter(item => item.status === 'active').length.toString(),
      change: '+2.1%',
      trend: 'up' as const,
      icon: CheckCircle
    },
    {
      title: 'This Month',
      value: vendors.filter(item => {
        if (!item.created_at) return false
        const created = new Date(item.created_at)
        const now = new Date()
        return created.getMonth() === now.getMonth() && created.getFullYear() === now.getFullYear()
      }).length.toString(),
      change: '+8.3%',
      trend: 'up' as const,
      icon: TrendingUp
    }
  ]

  // Enhanced table columns with business rule columns
  const columns: TableColumn[] = [
    { key: 'entity_name', label: 'Vendor Name', sortable: true },
    { key: 'vendor_code', label: 'Vendor code', sortable: true },
    { key: 'tax_id', label: 'Tax id', sortable: true },
    { key: 'email', label: 'Email', sortable: true },
    { key: 'phone', label: 'Phone', sortable: true },
    { key: 'payment_terms', label: 'Payment terms', sortable: true },
    { key: 'created_at', label: 'Created', sortable: true },
    { key: 'actions', label: 'Actions', sortable: false }
  ]

  // Enhanced filter fields with business rule filters
  const filterFields: FilterField[] = [
    { key: 'search', label: 'Search Vendors', type: 'search' },
    { key: 'status', label: 'Status', type: 'select', options: [
      { value: '', label: 'All Status' },
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'Inactive' }
    ]},
    { key: 'vendor_code', label: 'Vendor Code', type: 'select', options: [
        { value: '', label: 'All Vendor Codes' },
        ...Array.from(new Set(vendors.map(item => item.vendor_code).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]},
    { key: 'tax_id', label: 'Tax Id', type: 'select', options: [
        { value: '', label: 'All Tax Ids' },
        ...Array.from(new Set(vendors.map(item => item.tax_id).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]}
  ]

  // Enterprise CRUD Operations with Events
  const handleAddVendor = async (vendorData: any) => {
    try {
      const result = await vendorData.create({
        entity_type: 'VENDOR',
        entity_name: vendorData.entity_name,
        smart_code: VENDOR_SMART_CODES.ENTITY,
        organization_id: currentOrganization?.id
      }, vendorData)

      // Emit creation event for audit trail
      await vendorData.emitEvent(VENDOR_SMART_CODES.EVENT_CREATED, {
        entity_id: result.id,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        data: vendorData
      })

      setShowAddModal(false)
      console.log('✅ Vendor created successfully')
    } catch (error) {
      console.error('❌ Error adding vendor:', error)
    }
  }

  const handleEditVendor = async (vendorData: any) => {
    if (!currentVendor) return
    
    try {
      await vendorData.update(currentVendor.entity_id!, {
        entity_name: vendorData.entity_name
      }, vendorData)

      // Emit update event
      await vendorData.emitEvent(VENDOR_SMART_CODES.EVENT_UPDATED, {
        entity_id: currentVendor.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        changes: vendorData
      })

      setShowEditModal(false)
      setCurrentVendor(null)
      console.log('✅ Vendor updated successfully')
    } catch (error) {
      console.error('❌ Error updating vendor:', error)
    }
  }

  const handleDeleteVendor = async () => {
    if (!vendorToDelete) return
    
    try {
      await vendorData.delete(vendorToDelete.entity_id!)

      // Emit deletion event
      await vendorData.emitEvent(VENDOR_SMART_CODES.EVENT_DELETED, {
        entity_id: vendorToDelete.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        entity_name: vendorToDelete.entity_name
      })

      setShowDeleteModal(false)
      setVendorToDelete(null)
      console.log('✅ Vendor deleted successfully')
    } catch (error) {
      console.error('❌ Error deleting vendor:', error)
    }
  }

  
  

  // Enterprise security checks
  if (!isAuthenticated) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
        <p>Please log in to access Vendors.</p>
      </div>
    )
  }

  if (vendorData.contextLoading) {
    return (
      <div className="p-4 text-center">
        <div className="animate-spin h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
        <p>Loading Vendors...</p>
      </div>
    )
  }

  if (!currentOrganization) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-yellow-500 mx-auto mb-2" />
        <p>No organization context found. Please select an organization.</p>
      </div>
    )
  }

  return (
    <MobilePageLayout
      title="Vendors"
      subtitle={`${vendors.length} total vendors`}
      primaryColor="#0d7377"
      accentColor="#0a5d61"
      showBackButton={false}
    >
      {/* Enterprise KPI Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {kpis.map((kpi, index) => (
          <MobileCard key={index} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 font-medium">{kpi.title}</p>
                <p className="text-2xl font-bold" style={{ color: '#0d7377' }}>{kpi.value}</p>
                <p className={`text-xs font-medium ${kpi.trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                  {kpi.change}
                </p>
              </div>
              <kpi.icon className="h-8 w-8 text-gray-400" />
            </div>
          </MobileCard>
        ))}
      </div>

      {/* Enhanced Filters */}
      <MobileFilters
        fields={filterFields}
        values={filters}
        onChange={setFilters}
        className="mb-6"
      />

      {/* Enterprise Data Table */}
      <MobileDataTable
        data={vendors}
        columns={columns}
        selectedRows={selectedVendors}
        onRowSelect={setSelectedVendors}
        onRowClick={(vendor) => {
          setCurrentVendor(vendor)
          setShowEditModal(true)
        }}
        showBulkActions={selectedVendors.length > 0}
        bulkActions={[
          {
            label: 'Delete Selected',
            action: async () => {
              // Bulk delete with events
              for (const id of selectedVendors) {
                await vendorData.delete(id.toString())
              }
              setSelectedVendors([])
            },
            variant: 'destructive'
          }
        ]}
        mobileCardRender={(vendor) => (
          <MobileCard key={vendor.id} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-lg">{vendor.entity_name}</h3>
                
                
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setCurrentVendor(vendor)
                    setShowEditModal(true)
                  }}
                  className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                >
                  <Edit className="h-4 w-4" />
                </button>
                <button
                  onClick={() => {
                    setVendorToDelete(vendor)
                    setShowDeleteModal(true)
                  }}
                  className="p-1 text-red-600 hover:bg-red-50 rounded"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            {/* Dynamic fields display */}
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Vendor Code:</span>{' '}
              {vendor.vendor_code || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Tax Id:</span>{' '}
              {vendor.tax_id || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Email:</span>{' '}
              {vendor.email || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Phone:</span>{' '}
              {vendor.phone || 'N/A'}
            </div>
            
            <div className="text-xs text-gray-400 mt-2 pt-2 border-t">
              Created: {vendor.created_at ? new Date(vendor.created_at).toLocaleDateString() : 'N/A'}
            </div>
          </MobileCard>
        )}
      />

      {/* Floating Action Button */}
      <button
        onClick={() => setShowAddModal(true)}
        className="fixed bottom-6 right-6 text-white rounded-full p-4 shadow-lg transition-colors z-50 hover:shadow-xl"
        style={{ backgroundColor: '#0d7377' }}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Enterprise Modals */}
      {showAddModal && (
        <VendorModal
          title="Add New Vendor"
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          onSave={handleAddVendor}
          dynamicFields={vendorData.dynamicFieldsConfig || []}
          businessRules={{"duplicate_detection":true,"audit_trail":true,"credit_monitoring":true}}
        />
      )}

      {showEditModal && currentVendor && (
        <VendorModal
          title="Edit Vendor"
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false)
            setCurrentVendor(null)
          }}
          onSave={handleEditVendor}
          initialData={currentVendor}
          dynamicFields={vendorData.dynamicFieldsConfig || []}
          businessRules={{"duplicate_detection":true,"audit_trail":true,"credit_monitoring":true}}
        />
      )}

      {showDeleteModal && vendorToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex items-center mb-4">
              <AlertCircle className="h-6 w-6 text-red-500 mr-2" />
              <h3 className="text-lg font-semibold">Delete Vendor</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete "{vendorToDelete.entity_name}"? This action cannot be undone.
            </p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => {
                  setShowDeleteModal(false)
                  setVendorToDelete(null)
                }}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteVendor}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </MobilePageLayout>
  )
}

/**
 * Enterprise Vendor Modal Component
 * Enhanced with business rules and validation
 */
interface VendorModalProps {
  title: string
  isOpen: boolean
  onClose: () => void
  onSave: (data: any) => void
  initialData?: Vendor
  dynamicFields: any[]
  businessRules: any
}

function VendorModal({ 
  title, 
  isOpen, 
  onClose, 
  onSave, 
  initialData, 
  dynamicFields,
  businessRules 
}: VendorModalProps) {
  const [formData, setFormData] = useState(() => {
    const initial: any = { 
      entity_name: initialData?.entity_name || '' 
    }
    
    dynamicFields.forEach(field => {
      initial[field.name] = initialData?.[field.name as keyof Vendor] || (field.type === 'number' ? 0 : '')
    })
    
    return initial
  })

  const [errors, setErrors] = useState<Record<string, string>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  const validateForm = () => {
    const newErrors: Record<string, string> = {}
    
    // Validate required fields
    dynamicFields.forEach(field => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label || field.name} is required`
      }
    })
    
    // Entity name validation
    if (!formData.entity_name?.trim()) {
      newErrors.entity_name = 'Vendor name is required'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!validateForm()) return
    
    setIsSubmitting(true)
    try {
      await onSave(formData)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-xl font-semibold">{title}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Entity Name Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Vendor Name *
              </label>
              <input
                type="text"
                value={formData.entity_name}
                onChange={(e) => setFormData({ ...formData, entity_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.entity_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.entity_name && (
                <p className="mt-1 text-sm text-red-600">{errors.entity_name}</p>
              )}
            </div>

            {/* Dynamic Fields with Enhanced Validation */}
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Vendor Code 
              </label>
              <input
                type="text"
                value={formData.vendor_code}
                onChange={(e) => setFormData({ ...formData, vendor_code: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.vendor_code ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.vendor_code && (
                <p className="mt-1 text-sm text-red-600">{errors.vendor_code}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Tax Id 
              </label>
              <input
                type="text"
                value={formData.tax_id}
                onChange={(e) => setFormData({ ...formData, tax_id: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.tax_id ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.tax_id && (
                <p className="mt-1 text-sm text-red-600">{errors.tax_id}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Email *
              </label>
              <input
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.email ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.email && (
                <p className="mt-1 text-sm text-red-600">{errors.email}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Phone *
              </label>
              <input
                type="tel"
                value={formData.phone}
                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.phone ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.phone && (
                <p className="mt-1 text-sm text-red-600">{errors.phone}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Payment Terms 
              </label>
              <input
                type="text"
                value={formData.payment_terms}
                onChange={(e) => setFormData({ ...formData, payment_terms: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.payment_terms ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.payment_terms && (
                <p className="mt-1 text-sm text-red-600">{errors.payment_terms}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Credit Limit 
              </label>
              <input
                type="text"
                value={formData.credit_limit}
                onChange={(e) => setFormData({ ...formData, credit_limit: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.credit_limit ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.credit_limit && (
                <p className="mt-1 text-sm text-red-600">{errors.credit_limit}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Category 
              </label>
              <input
                type="text"
                value={formData.category}
                onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.category ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.category && (
                <p className="mt-1 text-sm text-red-600">{errors.category}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Location 
              </label>
              <input
                type="text"
                value={formData.location}
                onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.location ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.location && (
                <p className="mt-1 text-sm text-red-600">{errors.location}</p>
              )}
            </div>

            {/* Business Rules Info */}
            

            {/* Form Actions */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-white rounded-md hover:opacity-90 flex items-center disabled:opacity-50"
                style={{ backgroundColor: '#0d7377' }}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}