'use client'

/**
 * Contracts CRUD Page
 * Generated by HERA Enterprise Generator
 * 
 * Module: WASTE_MANAGEMENT
 * Entity: CONTRACT
 * Smart Code: HERA.WASTE.MASTER.ENTITY.CONTRACT.v1
 * Description: Service agreements with pricing models and SLA terms
 */

import React, { useState, useCallback, useEffect } from 'react'
import { MobilePageLayout } from '@/components/mobile/MobilePageLayout'
import { MobileFilters, type FilterField } from '@/components/mobile/MobileFilters'
import { MobileDataTable, type TableColumn, type TableRecord } from '@/components/mobile/MobileDataTable'
import { MobileCard } from '@/components/mobile/MobileCard'
import { MobileChart } from '@/components/mobile/MobileCharts'
import { useHERAAuth } from '@/components/auth/HERAAuthProvider'
import { useUniversalEntity } from '@/hooks/useUniversalEntity'
import { 
  AlertCircle,
  CheckCircle,
  Clock,
  Download,
  Edit,
  Eye,
  FileText,
  Filter,
  MoreVertical,
  Plus,
  Save,
  Search,
  Trash2,
  TrendingUp,
  Upload,
  X
} from 'lucide-react'

/**
 * Contract Entity Interface
 * Extends TableRecord for HERA compliance
 */
interface Contract extends TableRecord {
  id: string
  entity_id?: string
  entity_name: string
  smart_code: string
  status?: string
  
  // Dynamic fields (stored in core_dynamic_data)
  contract_number?: string
  start_date?: string
  end_date?: string
  pricing_model?: string
  rate_inr?: string
  max_weight_kg?: string
  sla_response_minutes?: string
  
  // System fields
  created_at?: string
  updated_at?: string
  created_by?: string
  updated_by?: string
  
  // Business rule fields
  
  
}

/**
 * HERA Contract Smart Codes
 * Auto-generated from preset configuration
 */
const CONTRACT_SMART_CODES = {
  ENTITY: 'HERA.WASTE.MASTER.ENTITY.CONTRACT.v1',
  FIELD_CONTRACT_NUMBER: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.CONTRACT_NUMBER.V1',
  FIELD_START_DATE: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.START_DATE.V1',
  FIELD_END_DATE: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.END_DATE.V1',
  FIELD_PRICING_MODEL: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.PRICING_MODEL.V1',
  FIELD_RATE_INR: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.RATE_INR.V1',
  FIELD_MAX_WEIGHT_KG: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.MAX_WEIGHT_KG.V1',
  FIELD_SLA_RESPONSE_MINUTES: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.SLA_RESPONSE_MINUTES.V1',
  
  // Event smart codes for audit trail
  EVENT_CREATED: 'HERA.WASTE.MASTER.EVENT.CONTRACT.v1.CREATED.V1',
  EVENT_UPDATED: 'HERA.WASTE.MASTER.EVENT.CONTRACT.v1.UPDATED.V1',
  EVENT_DELETED: 'HERA.WASTE.MASTER.EVENT.CONTRACT.v1.DELETED.V1'
} as const

/**
 * Contracts Main Page Component
 * Enterprise-grade CRUD with quality gates and business rules
 */
export default function ContractsPage() {
  const { currentOrganization, isAuthenticated, user } = useHERAAuth()
  const [selectedContracts, setSelectedContracts] = useState<(string | number)[]>([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)
  const [showDeleteModal, setShowDeleteModal] = useState(false)
  const [currentContract, setCurrentContract] = useState<Contract | null>(null)
  const [contractToDelete, setContractToDelete] = useState<Contract | null>(null)
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    // Dynamic filter fields
    contract_number: '',
    start_date: '',
    end_date: ''
  })

  // HERA Universal Entity Integration
  const contractData = useUniversalEntity({
    entity_type: 'CONTRACT',
    organizationId: currentOrganization?.id,
    filters: {
      include_dynamic: true,
      include_relationships: undefined,
      status: 'active'
    },
    dynamicFields: [
      { name: 'contract_number', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.CONTRACT_NUMBER.V1', required: false },
      { name: 'start_date', type: 'date', smart_code: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.START_DATE.V1', required: false },
      { name: 'end_date', type: 'date', smart_code: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.END_DATE.V1', required: false },
      { name: 'pricing_model', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.PRICING_MODEL.V1', required: false },
      { name: 'rate_inr', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.RATE_INR.V1', required: false },
      { name: 'max_weight_kg', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.MAX_WEIGHT_KG.V1', required: false },
      { name: 'sla_response_minutes', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.CONTRACT.v1.SLA_RESPONSE_MINUTES.V1', required: false }
    ]
  })

  // Transform entities with business rule extensions
  const contracts: Contract[] = contractData.entities?.map((entity: any) => {
    return {
      id: entity.id,
      entity_id: entity.id,
      entity_name: entity.entity_name || '',
      smart_code: entity.smart_code || '',
      status: entity.status || 'active',
      
      // Map dynamic fields with type safety
      contract_number: entity.dynamic_data?.find((d: any) => d.field_name === 'contract_number')?.field_value_text || '',
      start_date: entity.dynamic_data?.find((d: any) => d.field_name === 'start_date')?.field_value_text || '',
      end_date: entity.dynamic_data?.find((d: any) => d.field_name === 'end_date')?.field_value_text || '',
      pricing_model: entity.dynamic_data?.find((d: any) => d.field_name === 'pricing_model')?.field_value_text || '',
      rate_inr: entity.dynamic_data?.find((d: any) => d.field_name === 'rate_inr')?.field_value_text || '',
      max_weight_kg: entity.dynamic_data?.find((d: any) => d.field_name === 'max_weight_kg')?.field_value_text || '',
      sla_response_minutes: entity.dynamic_data?.find((d: any) => d.field_name === 'sla_response_minutes')?.field_value_text || '',
      
      // System fields
      created_at: entity.created_at,
      updated_at: entity.updated_at,
      created_by: entity.created_by,
      updated_by: entity.updated_by
      
      
    }
  }) || []

  // Enhanced KPI calculations with preset metrics
  const kpis = [
    {
      title: 'Total Contracts',
      value: contracts.length.toString(),
      change: '+5.2%',
      trend: 'up' as const,
      icon: FileText
    },
    {
      title: 'Active Contracts',
      value: contracts.filter(item => item.status === 'active').length.toString(),
      change: '+2.1%',
      trend: 'up' as const,
      icon: CheckCircle
    },
    {
      title: 'This Month',
      value: contracts.filter(item => {
        if (!item.created_at) return false
        const created = new Date(item.created_at)
        const now = new Date()
        return created.getMonth() === now.getMonth() && created.getFullYear() === now.getFullYear()
      }).length.toString(),
      change: '+8.3%',
      trend: 'up' as const,
      icon: TrendingUp
    }
  ]

  // Enhanced table columns with business rule columns
  const columns: TableColumn[] = [
    { key: 'entity_name', label: 'Contract Name', sortable: true },
    { key: 'contract_number', label: 'Contract number', sortable: true },
    { key: 'start_date', label: 'Start date', sortable: true },
    { key: 'end_date', label: 'End date', sortable: true },
    { key: 'pricing_model', label: 'Pricing model', sortable: true },
    { key: 'rate_inr', label: 'Rate inr', sortable: true },
    { key: 'created_at', label: 'Created', sortable: true },
    { key: 'actions', label: 'Actions', sortable: false }
  ]

  // Enhanced filter fields with business rule filters
  const filterFields: FilterField[] = [
    { key: 'search', label: 'Search Contracts', type: 'search' },
    { key: 'status', label: 'Status', type: 'select', options: [
      { value: '', label: 'All Status' },
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'Inactive' }
    ]},
    { key: 'contract_number', label: 'Contract Number', type: 'select', options: [
        { value: '', label: 'All Contract Numbers' },
        ...Array.from(new Set(contracts.map(item => item.contract_number).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]}
  ]

  // Enterprise CRUD Operations with Events
  const handleAddContract = async (contractData: any) => {
    try {
      const result = await contractData.create({
        entity_type: 'CONTRACT',
        entity_name: contractData.entity_name,
        smart_code: CONTRACT_SMART_CODES.ENTITY,
        organization_id: currentOrganization?.id
      }, contractData)

      // Emit creation event for audit trail
      await contractData.emitEvent(CONTRACT_SMART_CODES.EVENT_CREATED, {
        entity_id: result.id,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        data: contractData
      })

      setShowAddModal(false)
      console.log('✅ Contract created successfully')
    } catch (error) {
      console.error('❌ Error adding contract:', error)
    }
  }

  const handleEditContract = async (contractData: any) => {
    if (!currentContract) return
    
    try {
      await contractData.update(currentContract.entity_id!, {
        entity_name: contractData.entity_name
      }, contractData)

      // Emit update event
      await contractData.emitEvent(CONTRACT_SMART_CODES.EVENT_UPDATED, {
        entity_id: currentContract.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        changes: contractData
      })

      setShowEditModal(false)
      setCurrentContract(null)
      console.log('✅ Contract updated successfully')
    } catch (error) {
      console.error('❌ Error updating contract:', error)
    }
  }

  const handleDeleteContract = async () => {
    if (!contractToDelete) return
    
    try {
      await contractData.delete(contractToDelete.entity_id!)

      // Emit deletion event
      await contractData.emitEvent(CONTRACT_SMART_CODES.EVENT_DELETED, {
        entity_id: contractToDelete.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        entity_name: contractToDelete.entity_name
      })

      setShowDeleteModal(false)
      setContractToDelete(null)
      console.log('✅ Contract deleted successfully')
    } catch (error) {
      console.error('❌ Error deleting contract:', error)
    }
  }

  
  

  // Enterprise security checks
  if (!isAuthenticated) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
        <p>Please log in to access Contracts.</p>
      </div>
    )
  }

  if (contractData.contextLoading) {
    return (
      <div className="p-4 text-center">
        <div className="animate-spin h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
        <p>Loading Contracts...</p>
      </div>
    )
  }

  if (!currentOrganization) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-yellow-500 mx-auto mb-2" />
        <p>No organization context found. Please select an organization.</p>
      </div>
    )
  }

  return (
    <MobilePageLayout
      title="Contracts"
      subtitle={`${contracts.length} total contracts`}
      primaryColor="#0891b2"
      accentColor="#0e7490"
      showBackButton={false}
    >
      {/* Enterprise KPI Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {kpis.map((kpi, index) => (
          <MobileCard key={index} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 font-medium">{kpi.title}</p>
                <p className="text-2xl font-bold" style={{ color: '#0891b2' }}>{kpi.value}</p>
                <p className={`text-xs font-medium ${kpi.trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                  {kpi.change}
                </p>
              </div>
              <kpi.icon className="h-8 w-8 text-gray-400" />
            </div>
          </MobileCard>
        ))}
      </div>

      {/* Enhanced Filters */}
      <MobileFilters
        fields={filterFields}
        values={filters}
        onChange={setFilters}
        className="mb-6"
      />

      {/* Enterprise Data Table */}
      <MobileDataTable
        data={contracts}
        columns={columns}
        selectedRows={selectedContracts}
        onRowSelect={setSelectedContracts}
        onRowClick={(contract) => {
          setCurrentContract(contract)
          setShowEditModal(true)
        }}
        showBulkActions={selectedContracts.length > 0}
        bulkActions={[
          {
            label: 'Delete Selected',
            action: async () => {
              // Bulk delete with events
              for (const id of selectedContracts) {
                await contractData.delete(id.toString())
              }
              setSelectedContracts([])
            },
            variant: 'destructive'
          }
        ]}
        mobileCardRender={(contract) => (
          <MobileCard key={contract.id} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-lg">{contract.entity_name}</h3>
                
                
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setCurrentContract(contract)
                    setShowEditModal(true)
                  }}
                  className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                >
                  <Edit className="h-4 w-4" />
                </button>
                <button
                  onClick={() => {
                    setContractToDelete(contract)
                    setShowDeleteModal(true)
                  }}
                  className="p-1 text-red-600 hover:bg-red-50 rounded"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            {/* Dynamic fields display */}
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Contract Number:</span>{' '}
              {contract.contract_number || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Start Date:</span>{' '}
              {contract.start_date || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">End Date:</span>{' '}
              {contract.end_date || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Pricing Model:</span>{' '}
              {contract.pricing_model || 'N/A'}
            </div>
            
            <div className="text-xs text-gray-400 mt-2 pt-2 border-t">
              Created: {contract.created_at ? new Date(contract.created_at).toLocaleDateString() : 'N/A'}
            </div>
          </MobileCard>
        )}
      />

      {/* Floating Action Button */}
      <button
        onClick={() => setShowAddModal(true)}
        className="fixed bottom-6 right-6 text-white rounded-full p-4 shadow-lg transition-colors z-50 hover:shadow-xl"
        style={{ backgroundColor: '#0891b2' }}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Enterprise Modals */}
      {showAddModal && (
        <ContractModal
          title="Add New Contract"
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          onSave={handleAddContract}
          dynamicFields={contractData.dynamicFieldsConfig || []}
          businessRules={{"date_validation":true,"audit_trail":true,"approval_workflow":true}}
        />
      )}

      {showEditModal && currentContract && (
        <ContractModal
          title="Edit Contract"
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false)
            setCurrentContract(null)
          }}
          onSave={handleEditContract}
          initialData={currentContract}
          dynamicFields={contractData.dynamicFieldsConfig || []}
          businessRules={{"date_validation":true,"audit_trail":true,"approval_workflow":true}}
        />
      )}

      {showDeleteModal && contractToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex items-center mb-4">
              <AlertCircle className="h-6 w-6 text-red-500 mr-2" />
              <h3 className="text-lg font-semibold">Delete Contract</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete "{contractToDelete.entity_name}"? This action cannot be undone.
            </p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => {
                  setShowDeleteModal(false)
                  setContractToDelete(null)
                }}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteContract}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </MobilePageLayout>
  )
}

/**
 * Enterprise Contract Modal Component
 * Enhanced with business rules and validation
 */
interface ContractModalProps {
  title: string
  isOpen: boolean
  onClose: () => void
  onSave: (data: any) => void
  initialData?: Contract
  dynamicFields: any[]
  businessRules: any
}

function ContractModal({ 
  title, 
  isOpen, 
  onClose, 
  onSave, 
  initialData, 
  dynamicFields,
  businessRules 
}: ContractModalProps) {
  const [formData, setFormData] = useState(() => {
    const initial: any = { 
      entity_name: initialData?.entity_name || '' 
    }
    
    dynamicFields.forEach(field => {
      initial[field.name] = initialData?.[field.name as keyof Contract] || (field.type === 'number' ? 0 : '')
    })
    
    return initial
  })

  const [errors, setErrors] = useState<Record<string, string>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  const validateForm = () => {
    const newErrors: Record<string, string> = {}
    
    // Validate required fields
    dynamicFields.forEach(field => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label || field.name} is required`
      }
    })
    
    // Entity name validation
    if (!formData.entity_name?.trim()) {
      newErrors.entity_name = 'Contract name is required'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!validateForm()) return
    
    setIsSubmitting(true)
    try {
      await onSave(formData)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-xl font-semibold">{title}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Entity Name Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Contract Name *
              </label>
              <input
                type="text"
                value={formData.entity_name}
                onChange={(e) => setFormData({ ...formData, entity_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.entity_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.entity_name && (
                <p className="mt-1 text-sm text-red-600">{errors.entity_name}</p>
              )}
            </div>

            {/* Dynamic Fields with Enhanced Validation */}
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Contract Number 
              </label>
              <input
                type="text"
                value={formData.contract_number}
                onChange={(e) => setFormData({ ...formData, contract_number: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.contract_number ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.contract_number && (
                <p className="mt-1 text-sm text-red-600">{errors.contract_number}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Start Date 
              </label>
              <input
                type="date"
                value={formData.start_date}
                onChange={(e) => setFormData({ ...formData, start_date: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.start_date ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.start_date && (
                <p className="mt-1 text-sm text-red-600">{errors.start_date}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                End Date 
              </label>
              <input
                type="date"
                value={formData.end_date}
                onChange={(e) => setFormData({ ...formData, end_date: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.end_date ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.end_date && (
                <p className="mt-1 text-sm text-red-600">{errors.end_date}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Pricing Model 
              </label>
              <input
                type="text"
                value={formData.pricing_model}
                onChange={(e) => setFormData({ ...formData, pricing_model: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.pricing_model ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.pricing_model && (
                <p className="mt-1 text-sm text-red-600">{errors.pricing_model}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Rate Inr 
              </label>
              <input
                type="text"
                value={formData.rate_inr}
                onChange={(e) => setFormData({ ...formData, rate_inr: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.rate_inr ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.rate_inr && (
                <p className="mt-1 text-sm text-red-600">{errors.rate_inr}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Max Weight Kg 
              </label>
              <input
                type="text"
                value={formData.max_weight_kg}
                onChange={(e) => setFormData({ ...formData, max_weight_kg: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.max_weight_kg ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.max_weight_kg && (
                <p className="mt-1 text-sm text-red-600">{errors.max_weight_kg}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Sla Response Minutes 
              </label>
              <input
                type="text"
                value={formData.sla_response_minutes}
                onChange={(e) => setFormData({ ...formData, sla_response_minutes: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.sla_response_minutes ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.sla_response_minutes && (
                <p className="mt-1 text-sm text-red-600">{errors.sla_response_minutes}</p>
              )}
            </div>

            {/* Business Rules Info */}
            

            {/* Form Actions */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-white rounded-md hover:opacity-90 flex items-center disabled:opacity-50"
                style={{ backgroundColor: '#0891b2' }}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}