  FIELD_CONTRACT_TYPE: 'HERA.WASTE.MASTER.CUSTOMER.v1.CONTRACT_TYPE.V1',
  FIELD_BILLING_TERMS: 'HERA.WASTE.MASTER.CUSTOMER.v1.BILLING_TERMS.V1',
