'use client'

/**
 * Locations CRUD Page
 * Generated by HERA Enterprise Generator
 * 
 * Module: WASTE_MANAGEMENT
 * Entity: LOCATION
 * Smart Code: HERA.WASTE.MASTER.ENTITY.LOCATION.v1
 * Description: Customer sites, MCF, MRF, RDF facilities, cement plants
 */

import React, { useState, useCallback, useEffect } from 'react'
import { MobilePageLayout } from '@/components/mobile/MobilePageLayout'
import { MobileFilters, type FilterField } from '@/components/mobile/MobileFilters'
import { MobileDataTable, type TableColumn, type TableRecord } from '@/components/mobile/MobileDataTable'
import { MobileCard } from '@/components/mobile/MobileCard'
import { MobileChart } from '@/components/mobile/MobileCharts'
import { useHERAAuth } from '@/components/auth/HERAAuthProvider'
import { useUniversalEntity } from '@/hooks/useUniversalEntity'
import { 
  AlertCircle,
  CheckCircle,
  Clock,
  Download,
  Edit,
  Eye,
  Filter,
  MapPin,
  MoreVertical,
  Plus,
  Save,
  Search,
  Trash2,
  TrendingUp,
  Upload,
  X
} from 'lucide-react'

/**
 * Location Entity Interface
 * Extends TableRecord for HERA compliance
 */
interface Location extends TableRecord {
  id: string
  entity_id?: string
  entity_name: string
  smart_code: string
  status?: string
  
  // Dynamic fields (stored in core_dynamic_data)
  location_name?: string
  location_type?: string
  address?: string
  geo?: string
  
  // System fields
  created_at?: string
  updated_at?: string
  created_by?: string
  updated_by?: string
  
  // Business rule fields
  
  
}

/**
 * HERA Location Smart Codes
 * Auto-generated from preset configuration
 */
const LOCATION_SMART_CODES = {
  ENTITY: 'HERA.WASTE.MASTER.ENTITY.LOCATION.v1',
  FIELD_LOCATION_NAME: 'HERA.WASTE.MASTER.DYN.LOCATION.v1.LOCATION_NAME.V1',
  FIELD_LOCATION_TYPE: 'HERA.WASTE.MASTER.DYN.LOCATION.v1.LOCATION_TYPE.V1',
  FIELD_ADDRESS: 'HERA.WASTE.MASTER.DYN.LOCATION.v1.ADDRESS.V1',
  FIELD_GEO: 'HERA.WASTE.MASTER.DYN.LOCATION.v1.GEO.V1',
  
  // Event smart codes for audit trail
  EVENT_CREATED: 'HERA.WASTE.MASTER.EVENT.LOCATION.v1.CREATED.V1',
  EVENT_UPDATED: 'HERA.WASTE.MASTER.EVENT.LOCATION.v1.UPDATED.V1',
  EVENT_DELETED: 'HERA.WASTE.MASTER.EVENT.LOCATION.v1.DELETED.V1'
} as const

/**
 * Locations Main Page Component
 * Enterprise-grade CRUD with quality gates and business rules
 */
export default function LocationsPage() {
  const { currentOrganization, isAuthenticated, user } = useHERAAuth()
  const [selectedLocations, setSelectedLocations] = useState<(string | number)[]>([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)
  const [showDeleteModal, setShowDeleteModal] = useState(false)
  const [currentLocation, setCurrentLocation] = useState<Location | null>(null)
  const [locationToDelete, setLocationToDelete] = useState<Location | null>(null)
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    // Dynamic filter fields
    location_name: '',
    location_type: '',
    address: ''
  })

  // HERA Universal Entity Integration
  const locationData = useUniversalEntity({
    entity_type: 'LOCATION',
    organizationId: currentOrganization?.id,
    filters: {
      include_dynamic: true,
      include_relationships: undefined,
      status: 'active'
    },
    dynamicFields: [
      { name: 'location_name', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.LOCATION.v1.LOCATION_NAME.V1', required: false },
      { name: 'location_type', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.LOCATION.v1.LOCATION_TYPE.V1', required: false },
      { name: 'address', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.LOCATION.v1.ADDRESS.V1', required: false },
      { name: 'geo', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.LOCATION.v1.GEO.V1', required: false }
    ]
  })

  // Transform entities with business rule extensions
  const locations: Location[] = locationData.entities?.map((entity: any) => {
    return {
      id: entity.id,
      entity_id: entity.id,
      entity_name: entity.entity_name || '',
      smart_code: entity.smart_code || '',
      status: entity.status || 'active',
      
      // Map dynamic fields with type safety
      location_name: entity.dynamic_data?.find((d: any) => d.field_name === 'location_name')?.field_value_text || '',
      location_type: entity.dynamic_data?.find((d: any) => d.field_name === 'location_type')?.field_value_text || '',
      address: entity.dynamic_data?.find((d: any) => d.field_name === 'address')?.field_value_text || '',
      geo: entity.dynamic_data?.find((d: any) => d.field_name === 'geo')?.field_value_text || '',
      
      // System fields
      created_at: entity.created_at,
      updated_at: entity.updated_at,
      created_by: entity.created_by,
      updated_by: entity.updated_by
      
      
    }
  }) || []

  // Enhanced KPI calculations with preset metrics
  const kpis = [
    {
      title: 'Total Locations',
      value: locations.length.toString(),
      change: '+5.2%',
      trend: 'up' as const,
      icon: MapPin
    },
    {
      title: 'Active Locations',
      value: locations.filter(item => item.status === 'active').length.toString(),
      change: '+2.1%',
      trend: 'up' as const,
      icon: CheckCircle
    },
    {
      title: 'This Month',
      value: locations.filter(item => {
        if (!item.created_at) return false
        const created = new Date(item.created_at)
        const now = new Date()
        return created.getMonth() === now.getMonth() && created.getFullYear() === now.getFullYear()
      }).length.toString(),
      change: '+8.3%',
      trend: 'up' as const,
      icon: TrendingUp
    }
  ]

  // Enhanced table columns with business rule columns
  const columns: TableColumn[] = [
    { key: 'entity_name', label: 'Location Name', sortable: true },
    { key: 'location_name', label: 'Location name', sortable: true },
    { key: 'location_type', label: 'Location type', sortable: true },
    { key: 'address', label: 'Address', sortable: true },
    { key: 'geo', label: 'Geo', sortable: true },
    { key: 'created_at', label: 'Created', sortable: true },
    { key: 'actions', label: 'Actions', sortable: false }
  ]

  // Enhanced filter fields with business rule filters
  const filterFields: FilterField[] = [
    { key: 'search', label: 'Search Locations', type: 'search' },
    { key: 'status', label: 'Status', type: 'select', options: [
      { value: '', label: 'All Status' },
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'Inactive' }
    ]},
    { key: 'location_name', label: 'Location Name', type: 'select', options: [
        { value: '', label: 'All Location Names' },
        ...Array.from(new Set(locations.map(item => item.location_name).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]},
    { key: 'location_type', label: 'Location Type', type: 'select', options: [
        { value: '', label: 'All Location Types' },
        ...Array.from(new Set(locations.map(item => item.location_type).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]}
  ]

  // Enterprise CRUD Operations with Events
  const handleAddLocation = async (locationData: any) => {
    try {
      const result = await locationData.create({
        entity_type: 'LOCATION',
        entity_name: locationData.entity_name,
        smart_code: LOCATION_SMART_CODES.ENTITY,
        organization_id: currentOrganization?.id
      }, locationData)

      // Emit creation event for audit trail
      await locationData.emitEvent(LOCATION_SMART_CODES.EVENT_CREATED, {
        entity_id: result.id,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        data: locationData
      })

      setShowAddModal(false)
      console.log('✅ Location created successfully')
    } catch (error) {
      console.error('❌ Error adding location:', error)
    }
  }

  const handleEditLocation = async (locationData: any) => {
    if (!currentLocation) return
    
    try {
      await locationData.update(currentLocation.entity_id!, {
        entity_name: locationData.entity_name
      }, locationData)

      // Emit update event
      await locationData.emitEvent(LOCATION_SMART_CODES.EVENT_UPDATED, {
        entity_id: currentLocation.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        changes: locationData
      })

      setShowEditModal(false)
      setCurrentLocation(null)
      console.log('✅ Location updated successfully')
    } catch (error) {
      console.error('❌ Error updating location:', error)
    }
  }

  const handleDeleteLocation = async () => {
    if (!locationToDelete) return
    
    try {
      await locationData.delete(locationToDelete.entity_id!)

      // Emit deletion event
      await locationData.emitEvent(LOCATION_SMART_CODES.EVENT_DELETED, {
        entity_id: locationToDelete.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        entity_name: locationToDelete.entity_name
      })

      setShowDeleteModal(false)
      setLocationToDelete(null)
      console.log('✅ Location deleted successfully')
    } catch (error) {
      console.error('❌ Error deleting location:', error)
    }
  }

  
  

  // Enterprise security checks
  if (!isAuthenticated) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
        <p>Please log in to access Locations.</p>
      </div>
    )
  }

  if (locationData.contextLoading) {
    return (
      <div className="p-4 text-center">
        <div className="animate-spin h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
        <p>Loading Locations...</p>
      </div>
    )
  }

  if (!currentOrganization) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-yellow-500 mx-auto mb-2" />
        <p>No organization context found. Please select an organization.</p>
      </div>
    )
  }

  return (
    <MobilePageLayout
      title="Locations"
      subtitle={`${locations.length} total locations`}
      primaryColor="#dc2626"
      accentColor="#b91c1c"
      showBackButton={false}
    >
      {/* Enterprise KPI Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {kpis.map((kpi, index) => (
          <MobileCard key={index} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 font-medium">{kpi.title}</p>
                <p className="text-2xl font-bold" style={{ color: '#dc2626' }}>{kpi.value}</p>
                <p className={`text-xs font-medium ${kpi.trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                  {kpi.change}
                </p>
              </div>
              <kpi.icon className="h-8 w-8 text-gray-400" />
            </div>
          </MobileCard>
        ))}
      </div>

      {/* Enhanced Filters */}
      <MobileFilters
        fields={filterFields}
        values={filters}
        onChange={setFilters}
        className="mb-6"
      />

      {/* Enterprise Data Table */}
      <MobileDataTable
        data={locations}
        columns={columns}
        selectedRows={selectedLocations}
        onRowSelect={setSelectedLocations}
        onRowClick={(location) => {
          setCurrentLocation(location)
          setShowEditModal(true)
        }}
        showBulkActions={selectedLocations.length > 0}
        bulkActions={[
          {
            label: 'Delete Selected',
            action: async () => {
              // Bulk delete with events
              for (const id of selectedLocations) {
                await locationData.delete(id.toString())
              }
              setSelectedLocations([])
            },
            variant: 'destructive'
          }
        ]}
        mobileCardRender={(location) => (
          <MobileCard key={location.id} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-lg">{location.entity_name}</h3>
                
                
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setCurrentLocation(location)
                    setShowEditModal(true)
                  }}
                  className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                >
                  <Edit className="h-4 w-4" />
                </button>
                <button
                  onClick={() => {
                    setLocationToDelete(location)
                    setShowDeleteModal(true)
                  }}
                  className="p-1 text-red-600 hover:bg-red-50 rounded"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            {/* Dynamic fields display */}
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Location Name:</span>{' '}
              {location.location_name || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Location Type:</span>{' '}
              {location.location_type || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Address:</span>{' '}
              {location.address || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Geo:</span>{' '}
              {location.geo || 'N/A'}
            </div>
            
            <div className="text-xs text-gray-400 mt-2 pt-2 border-t">
              Created: {location.created_at ? new Date(location.created_at).toLocaleDateString() : 'N/A'}
            </div>
          </MobileCard>
        )}
      />

      {/* Floating Action Button */}
      <button
        onClick={() => setShowAddModal(true)}
        className="fixed bottom-6 right-6 text-white rounded-full p-4 shadow-lg transition-colors z-50 hover:shadow-xl"
        style={{ backgroundColor: '#dc2626' }}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Enterprise Modals */}
      {showAddModal && (
        <LocationModal
          title="Add New Location"
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          onSave={handleAddLocation}
          dynamicFields={locationData.dynamicFieldsConfig || []}
          businessRules={{"geo_validation":true,"audit_trail":true}}
        />
      )}

      {showEditModal && currentLocation && (
        <LocationModal
          title="Edit Location"
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false)
            setCurrentLocation(null)
          }}
          onSave={handleEditLocation}
          initialData={currentLocation}
          dynamicFields={locationData.dynamicFieldsConfig || []}
          businessRules={{"geo_validation":true,"audit_trail":true}}
        />
      )}

      {showDeleteModal && locationToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex items-center mb-4">
              <AlertCircle className="h-6 w-6 text-red-500 mr-2" />
              <h3 className="text-lg font-semibold">Delete Location</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete "{locationToDelete.entity_name}"? This action cannot be undone.
            </p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => {
                  setShowDeleteModal(false)
                  setLocationToDelete(null)
                }}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteLocation}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </MobilePageLayout>
  )
}

/**
 * Enterprise Location Modal Component
 * Enhanced with business rules and validation
 */
interface LocationModalProps {
  title: string
  isOpen: boolean
  onClose: () => void
  onSave: (data: any) => void
  initialData?: Location
  dynamicFields: any[]
  businessRules: any
}

function LocationModal({ 
  title, 
  isOpen, 
  onClose, 
  onSave, 
  initialData, 
  dynamicFields,
  businessRules 
}: LocationModalProps) {
  const [formData, setFormData] = useState(() => {
    const initial: any = { 
      entity_name: initialData?.entity_name || '' 
    }
    
    dynamicFields.forEach(field => {
      initial[field.name] = initialData?.[field.name as keyof Location] || (field.type === 'number' ? 0 : '')
    })
    
    return initial
  })

  const [errors, setErrors] = useState<Record<string, string>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  const validateForm = () => {
    const newErrors: Record<string, string> = {}
    
    // Validate required fields
    dynamicFields.forEach(field => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label || field.name} is required`
      }
    })
    
    // Entity name validation
    if (!formData.entity_name?.trim()) {
      newErrors.entity_name = 'Location name is required'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!validateForm()) return
    
    setIsSubmitting(true)
    try {
      await onSave(formData)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-xl font-semibold">{title}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Entity Name Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Location Name *
              </label>
              <input
                type="text"
                value={formData.entity_name}
                onChange={(e) => setFormData({ ...formData, entity_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.entity_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.entity_name && (
                <p className="mt-1 text-sm text-red-600">{errors.entity_name}</p>
              )}
            </div>

            {/* Dynamic Fields with Enhanced Validation */}
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Location Name 
              </label>
              <input
                type="text"
                value={formData.location_name}
                onChange={(e) => setFormData({ ...formData, location_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.location_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.location_name && (
                <p className="mt-1 text-sm text-red-600">{errors.location_name}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Location Type 
              </label>
              <input
                type="text"
                value={formData.location_type}
                onChange={(e) => setFormData({ ...formData, location_type: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.location_type ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.location_type && (
                <p className="mt-1 text-sm text-red-600">{errors.location_type}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Address 
              </label>
              <input
                type="text"
                value={formData.address}
                onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.address ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.address && (
                <p className="mt-1 text-sm text-red-600">{errors.address}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Geo 
              </label>
              <input
                type="text"
                value={formData.geo}
                onChange={(e) => setFormData({ ...formData, geo: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.geo ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.geo && (
                <p className="mt-1 text-sm text-red-600">{errors.geo}</p>
              )}
            </div>

            {/* Business Rules Info */}
            

            {/* Form Actions */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-white rounded-md hover:opacity-90 flex items-center disabled:opacity-50"
                style={{ backgroundColor: '#dc2626' }}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}