'use client'

/**
 * Routes CRUD Page
 * Generated by HERA Enterprise Generator
 * 
 * Module: WASTE_MANAGEMENT
 * Entity: ROUTE
 * Smart Code: HERA.WASTE.MASTER.ENTITY.ROUTE.v1
 * Description: Collection routes with service windows and frequencies
 */

import React, { useState, useCallback, useEffect } from 'react'
import { MobilePageLayout } from '@/components/mobile/MobilePageLayout'
import { MobileFilters, type FilterField } from '@/components/mobile/MobileFilters'
import { MobileDataTable, type TableColumn, type TableRecord } from '@/components/mobile/MobileDataTable'
import { MobileCard } from '@/components/mobile/MobileCard'
import { MobileChart } from '@/components/mobile/MobileCharts'
import { useHERAAuth } from '@/components/auth/HERAAuthProvider'
import { useUniversalEntity } from '@/hooks/useUniversalEntity'
import { 
  AlertCircle,
  CheckCircle,
  Clock,
  Download,
  Edit,
  Eye,
  Filter,
  MoreVertical,
  Plus,
  Route,
  Save,
  Search,
  Trash2,
  TrendingUp,
  Upload,
  X
} from 'lucide-react'

/**
 * Route Entity Interface
 * Extends TableRecord for HERA compliance
 */
interface Route extends TableRecord {
  id: string
  entity_id?: string
  entity_name: string
  smart_code: string
  status?: string
  
  // Dynamic fields (stored in core_dynamic_data)
  route_code?: string
  frequency?: string
  service_window_start?: string
  service_window_end?: string
  
  // System fields
  created_at?: string
  updated_at?: string
  created_by?: string
  updated_by?: string
  
  // Business rule fields
  
  
}

/**
 * HERA Route Smart Codes
 * Auto-generated from preset configuration
 */
const ROUTE_SMART_CODES = {
  ENTITY: 'HERA.WASTE.MASTER.ENTITY.ROUTE.v1',
  FIELD_ROUTE_CODE: 'HERA.WASTE.MASTER.DYN.ROUTE.v1.ROUTE_CODE.V1',
  FIELD_FREQUENCY: 'HERA.WASTE.MASTER.DYN.ROUTE.v1.FREQUENCY.V1',
  FIELD_SERVICE_WINDOW_START: 'HERA.WASTE.MASTER.DYN.ROUTE.v1.SERVICE_WINDOW_START.V1',
  FIELD_SERVICE_WINDOW_END: 'HERA.WASTE.MASTER.DYN.ROUTE.v1.SERVICE_WINDOW_END.V1',
  
  // Event smart codes for audit trail
  EVENT_CREATED: 'HERA.WASTE.MASTER.EVENT.ROUTE.v1.CREATED.V1',
  EVENT_UPDATED: 'HERA.WASTE.MASTER.EVENT.ROUTE.v1.UPDATED.V1',
  EVENT_DELETED: 'HERA.WASTE.MASTER.EVENT.ROUTE.v1.DELETED.V1'
} as const

/**
 * Routes Main Page Component
 * Enterprise-grade CRUD with quality gates and business rules
 */
export default function RoutesPage() {
  const { currentOrganization, isAuthenticated, user } = useHERAAuth()
  const [selectedRoutes, setSelectedRoutes] = useState<(string | number)[]>([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)
  const [showDeleteModal, setShowDeleteModal] = useState(false)
  const [currentRoute, setCurrentRoute] = useState<Route | null>(null)
  const [routeToDelete, setRouteToDelete] = useState<Route | null>(null)
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    // Dynamic filter fields
    route_code: '',
    frequency: '',
    service_window_start: ''
  })

  // HERA Universal Entity Integration
  const routeData = useUniversalEntity({
    entity_type: 'ROUTE',
    organizationId: currentOrganization?.id,
    filters: {
      include_dynamic: true,
      include_relationships: undefined,
      status: 'active'
    },
    dynamicFields: [
      { name: 'route_code', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.ROUTE.v1.ROUTE_CODE.V1', required: false },
      { name: 'frequency', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.ROUTE.v1.FREQUENCY.V1', required: false },
      { name: 'service_window_start', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.ROUTE.v1.SERVICE_WINDOW_START.V1', required: false },
      { name: 'service_window_end', type: 'text', smart_code: 'HERA.WASTE.MASTER.DYN.ROUTE.v1.SERVICE_WINDOW_END.V1', required: false }
    ]
  })

  // Transform entities with business rule extensions
  const routes: Route[] = routeData.entities?.map((entity: any) => {
    return {
      id: entity.id,
      entity_id: entity.id,
      entity_name: entity.entity_name || '',
      smart_code: entity.smart_code || '',
      status: entity.status || 'active',
      
      // Map dynamic fields with type safety
      route_code: entity.dynamic_data?.find((d: any) => d.field_name === 'route_code')?.field_value_text || '',
      frequency: entity.dynamic_data?.find((d: any) => d.field_name === 'frequency')?.field_value_text || '',
      service_window_start: entity.dynamic_data?.find((d: any) => d.field_name === 'service_window_start')?.field_value_text || '',
      service_window_end: entity.dynamic_data?.find((d: any) => d.field_name === 'service_window_end')?.field_value_text || '',
      
      // System fields
      created_at: entity.created_at,
      updated_at: entity.updated_at,
      created_by: entity.created_by,
      updated_by: entity.updated_by
      
      
    }
  }) || []

  // Enhanced KPI calculations with preset metrics
  const kpis = [
    {
      title: 'Total Routes',
      value: routes.length.toString(),
      change: '+5.2%',
      trend: 'up' as const,
      icon: Route
    },
    {
      title: 'Active Routes',
      value: routes.filter(item => item.status === 'active').length.toString(),
      change: '+2.1%',
      trend: 'up' as const,
      icon: CheckCircle
    },
    {
      title: 'This Month',
      value: routes.filter(item => {
        if (!item.created_at) return false
        const created = new Date(item.created_at)
        const now = new Date()
        return created.getMonth() === now.getMonth() && created.getFullYear() === now.getFullYear()
      }).length.toString(),
      change: '+8.3%',
      trend: 'up' as const,
      icon: TrendingUp
    }
  ]

  // Enhanced table columns with business rule columns
  const columns: TableColumn[] = [
    { key: 'entity_name', label: 'Route Name', sortable: true },
    { key: 'route_code', label: 'Route code', sortable: true },
    { key: 'frequency', label: 'Frequency', sortable: true },
    { key: 'service_window_start', label: 'Service window start', sortable: true },
    { key: 'service_window_end', label: 'Service window end', sortable: true },
    { key: 'created_at', label: 'Created', sortable: true },
    { key: 'actions', label: 'Actions', sortable: false }
  ]

  // Enhanced filter fields with business rule filters
  const filterFields: FilterField[] = [
    { key: 'search', label: 'Search Routes', type: 'search' },
    { key: 'status', label: 'Status', type: 'select', options: [
      { value: '', label: 'All Status' },
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'Inactive' }
    ]},
    { key: 'route_code', label: 'Route Code', type: 'select', options: [
        { value: '', label: 'All Route Codes' },
        ...Array.from(new Set(routes.map(item => item.route_code).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]},
    { key: 'frequency', label: 'Frequency', type: 'select', options: [
        { value: '', label: 'All Frequencys' },
        ...Array.from(new Set(routes.map(item => item.frequency).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]}
  ]

  // Enterprise CRUD Operations with Events
  const handleAddRoute = async (routeData: any) => {
    try {
      const result = await routeData.create({
        entity_type: 'ROUTE',
        entity_name: routeData.entity_name,
        smart_code: ROUTE_SMART_CODES.ENTITY,
        organization_id: currentOrganization?.id
      }, routeData)

      // Emit creation event for audit trail
      await routeData.emitEvent(ROUTE_SMART_CODES.EVENT_CREATED, {
        entity_id: result.id,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        data: routeData
      })

      setShowAddModal(false)
      console.log('✅ Route created successfully')
    } catch (error) {
      console.error('❌ Error adding route:', error)
    }
  }

  const handleEditRoute = async (routeData: any) => {
    if (!currentRoute) return
    
    try {
      await routeData.update(currentRoute.entity_id!, {
        entity_name: routeData.entity_name
      }, routeData)

      // Emit update event
      await routeData.emitEvent(ROUTE_SMART_CODES.EVENT_UPDATED, {
        entity_id: currentRoute.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        changes: routeData
      })

      setShowEditModal(false)
      setCurrentRoute(null)
      console.log('✅ Route updated successfully')
    } catch (error) {
      console.error('❌ Error updating route:', error)
    }
  }

  const handleDeleteRoute = async () => {
    if (!routeToDelete) return
    
    try {
      await routeData.delete(routeToDelete.entity_id!)

      // Emit deletion event
      await routeData.emitEvent(ROUTE_SMART_CODES.EVENT_DELETED, {
        entity_id: routeToDelete.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        entity_name: routeToDelete.entity_name
      })

      setShowDeleteModal(false)
      setRouteToDelete(null)
      console.log('✅ Route deleted successfully')
    } catch (error) {
      console.error('❌ Error deleting route:', error)
    }
  }

  
  

  // Enterprise security checks
  if (!isAuthenticated) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
        <p>Please log in to access Routes.</p>
      </div>
    )
  }

  if (routeData.contextLoading) {
    return (
      <div className="p-4 text-center">
        <div className="animate-spin h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
        <p>Loading Routes...</p>
      </div>
    )
  }

  if (!currentOrganization) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-yellow-500 mx-auto mb-2" />
        <p>No organization context found. Please select an organization.</p>
      </div>
    )
  }

  return (
    <MobilePageLayout
      title="Routes"
      subtitle={`${routes.length} total routes`}
      primaryColor="#7c3aed"
      accentColor="#6d28d9"
      showBackButton={false}
    >
      {/* Enterprise KPI Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {kpis.map((kpi, index) => (
          <MobileCard key={index} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 font-medium">{kpi.title}</p>
                <p className="text-2xl font-bold" style={{ color: '#7c3aed' }}>{kpi.value}</p>
                <p className={`text-xs font-medium ${kpi.trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                  {kpi.change}
                </p>
              </div>
              <kpi.icon className="h-8 w-8 text-gray-400" />
            </div>
          </MobileCard>
        ))}
      </div>

      {/* Enhanced Filters */}
      <MobileFilters
        fields={filterFields}
        values={filters}
        onChange={setFilters}
        className="mb-6"
      />

      {/* Enterprise Data Table */}
      <MobileDataTable
        data={routes}
        columns={columns}
        selectedRows={selectedRoutes}
        onRowSelect={setSelectedRoutes}
        onRowClick={(route) => {
          setCurrentRoute(route)
          setShowEditModal(true)
        }}
        showBulkActions={selectedRoutes.length > 0}
        bulkActions={[
          {
            label: 'Delete Selected',
            action: async () => {
              // Bulk delete with events
              for (const id of selectedRoutes) {
                await routeData.delete(id.toString())
              }
              setSelectedRoutes([])
            },
            variant: 'destructive'
          }
        ]}
        mobileCardRender={(route) => (
          <MobileCard key={route.id} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-lg">{route.entity_name}</h3>
                
                
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setCurrentRoute(route)
                    setShowEditModal(true)
                  }}
                  className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                >
                  <Edit className="h-4 w-4" />
                </button>
                <button
                  onClick={() => {
                    setRouteToDelete(route)
                    setShowDeleteModal(true)
                  }}
                  className="p-1 text-red-600 hover:bg-red-50 rounded"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            {/* Dynamic fields display */}
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Route Code:</span>{' '}
              {route.route_code || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Frequency:</span>{' '}
              {route.frequency || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Service Window Start:</span>{' '}
              {route.service_window_start || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Service Window End:</span>{' '}
              {route.service_window_end || 'N/A'}
            </div>
            
            <div className="text-xs text-gray-400 mt-2 pt-2 border-t">
              Created: {route.created_at ? new Date(route.created_at).toLocaleDateString() : 'N/A'}
            </div>
          </MobileCard>
        )}
      />

      {/* Floating Action Button */}
      <button
        onClick={() => setShowAddModal(true)}
        className="fixed bottom-6 right-6 text-white rounded-full p-4 shadow-lg transition-colors z-50 hover:shadow-xl"
        style={{ backgroundColor: '#7c3aed' }}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Enterprise Modals */}
      {showAddModal && (
        <RouteModal
          title="Add New Route"
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          onSave={handleAddRoute}
          dynamicFields={routeData.dynamicFieldsConfig || []}
          businessRules={{"schedule_optimization":true,"audit_trail":true}}
        />
      )}

      {showEditModal && currentRoute && (
        <RouteModal
          title="Edit Route"
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false)
            setCurrentRoute(null)
          }}
          onSave={handleEditRoute}
          initialData={currentRoute}
          dynamicFields={routeData.dynamicFieldsConfig || []}
          businessRules={{"schedule_optimization":true,"audit_trail":true}}
        />
      )}

      {showDeleteModal && routeToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex items-center mb-4">
              <AlertCircle className="h-6 w-6 text-red-500 mr-2" />
              <h3 className="text-lg font-semibold">Delete Route</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete "{routeToDelete.entity_name}"? This action cannot be undone.
            </p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => {
                  setShowDeleteModal(false)
                  setRouteToDelete(null)
                }}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteRoute}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </MobilePageLayout>
  )
}

/**
 * Enterprise Route Modal Component
 * Enhanced with business rules and validation
 */
interface RouteModalProps {
  title: string
  isOpen: boolean
  onClose: () => void
  onSave: (data: any) => void
  initialData?: Route
  dynamicFields: any[]
  businessRules: any
}

function RouteModal({ 
  title, 
  isOpen, 
  onClose, 
  onSave, 
  initialData, 
  dynamicFields,
  businessRules 
}: RouteModalProps) {
  const [formData, setFormData] = useState(() => {
    const initial: any = { 
      entity_name: initialData?.entity_name || '' 
    }
    
    dynamicFields.forEach(field => {
      initial[field.name] = initialData?.[field.name as keyof Route] || (field.type === 'number' ? 0 : '')
    })
    
    return initial
  })

  const [errors, setErrors] = useState<Record<string, string>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  const validateForm = () => {
    const newErrors: Record<string, string> = {}
    
    // Validate required fields
    dynamicFields.forEach(field => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label || field.name} is required`
      }
    })
    
    // Entity name validation
    if (!formData.entity_name?.trim()) {
      newErrors.entity_name = 'Route name is required'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!validateForm()) return
    
    setIsSubmitting(true)
    try {
      await onSave(formData)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-xl font-semibold">{title}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Entity Name Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Route Name *
              </label>
              <input
                type="text"
                value={formData.entity_name}
                onChange={(e) => setFormData({ ...formData, entity_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.entity_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.entity_name && (
                <p className="mt-1 text-sm text-red-600">{errors.entity_name}</p>
              )}
            </div>

            {/* Dynamic Fields with Enhanced Validation */}
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Route Code 
              </label>
              <input
                type="text"
                value={formData.route_code}
                onChange={(e) => setFormData({ ...formData, route_code: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.route_code ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.route_code && (
                <p className="mt-1 text-sm text-red-600">{errors.route_code}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Frequency 
              </label>
              <input
                type="text"
                value={formData.frequency}
                onChange={(e) => setFormData({ ...formData, frequency: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.frequency ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.frequency && (
                <p className="mt-1 text-sm text-red-600">{errors.frequency}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Service Window Start 
              </label>
              <input
                type="text"
                value={formData.service_window_start}
                onChange={(e) => setFormData({ ...formData, service_window_start: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.service_window_start ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.service_window_start && (
                <p className="mt-1 text-sm text-red-600">{errors.service_window_start}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Service Window End 
              </label>
              <input
                type="text"
                value={formData.service_window_end}
                onChange={(e) => setFormData({ ...formData, service_window_end: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.service_window_end ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.service_window_end && (
                <p className="mt-1 text-sm text-red-600">{errors.service_window_end}</p>
              )}
            </div>

            {/* Business Rules Info */}
            

            {/* Form Actions */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-white rounded-md hover:opacity-90 flex items-center disabled:opacity-50"
                style={{ backgroundColor: '#7c3aed' }}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}