'use client'

/**
 * Waste Items CRUD Page
 * Generated by HERA Enterprise Generator
 * 
 * Module: WASTE_MANAGEMENT
 * Entity: GREENWORMS_WASTE_ITEM
 * Smart Code: HERA.WASTE.MASTER.WASTEITEM.v1
 * Description: Waste categories (mixed, plastic, paper, metal, organic, RDF)
 */

import React, { useState, useCallback, useEffect } from 'react'
import { MobilePageLayout } from '@/components/mobile/MobilePageLayout'
import { MobileFilters, type FilterField } from '@/components/mobile/MobileFilters'
import { MobileDataTable, type TableColumn, type TableRecord } from '@/components/mobile/MobileDataTable'
import { MobileCard } from '@/components/mobile/MobileCard'
import { MobileChart } from '@/components/mobile/MobileCharts'
import { useHERAAuth } from '@/components/auth/HERAAuthProvider'
import { useUniversalEntity } from '@/hooks/useUniversalEntity'
import { 
  AlertCircle,
  CheckCircle,
  Clock,
  Download,
  Edit,
  Eye,
  Filter,
  MoreVertical,
  Plus,
  Save,
  Search,
  Trash2,
  TrendingUp,
  Upload,
  X
} from 'lucide-react'

/**
 * WasteItem Entity Interface
 * Extends TableRecord for HERA compliance
 */
interface WasteItem extends TableRecord {
  id: string
  entity_id?: string
  entity_name: string
  smart_code: string
  status?: string
  
  // Dynamic fields (stored in core_dynamic_data)
  waste_code?: string
  category?: string
  uom?: string
  hazardous?: string
  
  // System fields
  created_at?: string
  updated_at?: string
  created_by?: string
  updated_by?: string
  
  // Business rule fields
  
  
}

/**
 * HERA Waste Item Smart Codes
 * Auto-generated from preset configuration
 */
const GREENWORMS_WASTE_ITEM_SMART_CODES = {
  ENTITY: 'HERA.WASTE.MASTER.WASTEITEM.v1',
  FIELD_WASTE_CODE: 'HERA.WASTE.MASTER.WASTEITEM.v1.WASTE_CODE.V1',
  FIELD_CATEGORY: 'HERA.WASTE.MASTER.WASTEITEM.v1.CATEGORY.V1',
  FIELD_UOM: 'HERA.WASTE.MASTER.WASTEITEM.v1.UOM.V1',
  FIELD_HAZARDOUS: 'HERA.WASTE.MASTER.WASTEITEM.v1.HAZARDOUS.V1',
  
  // Event smart codes for audit trail
  EVENT_CREATED: 'HERA.WASTE.MASTER.WASTEITEM.v1.CREATED.V1',
  EVENT_UPDATED: 'HERA.WASTE.MASTER.WASTEITEM.v1.UPDATED.V1',
  EVENT_DELETED: 'HERA.WASTE.MASTER.WASTEITEM.v1.DELETED.V1'
} as const

/**
 * Waste Items Main Page Component
 * Enterprise-grade CRUD with quality gates and business rules
 */
export default function WasteItemsPage() {
  const { currentOrganization, isAuthenticated, user } = useHERAAuth()
  const [selectedWasteItems, setSelectedWasteItems] = useState<(string | number)[]>([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)
  const [showDeleteModal, setShowDeleteModal] = useState(false)
  const [currentWasteItem, setCurrentWasteItem] = useState<WasteItem | null>(null)
  const [greenworms_waste_itemToDelete, setWasteItemToDelete] = useState<WasteItem | null>(null)
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    // Dynamic filter fields
    waste_code: '',
    category: '',
    uom: ''
  })

  // HERA Universal Entity Integration
  const greenworms_waste_itemData = useUniversalEntity({
    entity_type: 'GREENWORMS_WASTE_ITEM',
    organizationId: currentOrganization?.id,
    filters: {
      include_dynamic: true,
      include_relationships: undefined,
      status: 'active'
    },
    dynamicFields: [
      { name: 'waste_code', type: 'text', smart_code: 'HERA.WASTE.MASTER.WASTEITEM.v1.WASTE_CODE.V1', required: false },
      { name: 'category', type: 'text', smart_code: 'HERA.WASTE.MASTER.WASTEITEM.v1.CATEGORY.V1', required: false },
      { name: 'uom', type: 'text', smart_code: 'HERA.WASTE.MASTER.WASTEITEM.v1.UOM.V1', required: false },
      { name: 'hazardous', type: 'text', smart_code: 'HERA.WASTE.MASTER.WASTEITEM.v1.HAZARDOUS.V1', required: false }
    ]
  })

  // Transform entities with business rule extensions
  const greenworms_waste_items: WasteItem[] = greenworms_waste_itemData.entities?.map((entity: any) => {
    return {
      id: entity.id,
      entity_id: entity.id,
      entity_name: entity.entity_name || '',
      smart_code: entity.smart_code || '',
      status: entity.status || 'active',
      
      // Map dynamic fields with type safety
      waste_code: entity.dynamic_data?.find((d: any) => d.field_name === 'waste_code')?.field_value_text || '',
      category: entity.dynamic_data?.find((d: any) => d.field_name === 'category')?.field_value_text || '',
      uom: entity.dynamic_data?.find((d: any) => d.field_name === 'uom')?.field_value_text || '',
      hazardous: entity.dynamic_data?.find((d: any) => d.field_name === 'hazardous')?.field_value_text || '',
      
      // System fields
      created_at: entity.created_at,
      updated_at: entity.updated_at,
      created_by: entity.created_by,
      updated_by: entity.updated_by
      
      
    }
  }) || []

  // Enhanced KPI calculations with preset metrics
  const kpis = [
    {
      title: 'Total Waste Items',
      value: greenworms_waste_items.length.toString(),
      change: '+5.2%',
      trend: 'up' as const,
      icon: Trash2
    },
    {
      title: 'Active Waste Items',
      value: greenworms_waste_items.filter(item => item.status === 'active').length.toString(),
      change: '+2.1%',
      trend: 'up' as const,
      icon: CheckCircle
    },
    {
      title: 'This Month',
      value: greenworms_waste_items.filter(item => {
        if (!item.created_at) return false
        const created = new Date(item.created_at)
        const now = new Date()
        return created.getMonth() === now.getMonth() && created.getFullYear() === now.getFullYear()
      }).length.toString(),
      change: '+8.3%',
      trend: 'up' as const,
      icon: TrendingUp
    }
  ]

  // Enhanced table columns with business rule columns
  const columns: TableColumn[] = [
    { key: 'entity_name', label: 'Waste Item Name', sortable: true },
    { key: 'waste_code', label: 'Waste code', sortable: true },
    { key: 'category', label: 'Category', sortable: true },
    { key: 'uom', label: 'Uom', sortable: true },
    { key: 'hazardous', label: 'Hazardous', sortable: true },
    { key: 'created_at', label: 'Created', sortable: true },
    { key: 'actions', label: 'Actions', sortable: false }
  ]

  // Enhanced filter fields with business rule filters
  const filterFields: FilterField[] = [
    { key: 'search', label: 'Search Waste Items', type: 'search' },
    { key: 'status', label: 'Status', type: 'select', options: [
      { value: '', label: 'All Status' },
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'Inactive' }
    ]},
    { key: 'waste_code', label: 'Waste Code', type: 'select', options: [
        { value: '', label: 'All Waste Codes' },
        ...Array.from(new Set(greenworms_waste_items.map(item => item.waste_code).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]},
    { key: 'category', label: 'Category', type: 'select', options: [
        { value: '', label: 'All Categorys' },
        ...Array.from(new Set(greenworms_waste_items.map(item => item.category).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]}
  ]

  // Enterprise CRUD Operations with Events
  const handleAddWasteItem = async (greenworms_waste_itemData: any) => {
    try {
      const result = await greenworms_waste_itemData.create({
        entity_type: 'GREENWORMS_WASTE_ITEM',
        entity_name: greenworms_waste_itemData.entity_name,
        smart_code: GREENWORMS_WASTE_ITEM_SMART_CODES.ENTITY,
        organization_id: currentOrganization?.id
      }, greenworms_waste_itemData)

      // Emit creation event for audit trail
      await greenworms_waste_itemData.emitEvent(GREENWORMS_WASTE_ITEM_SMART_CODES.EVENT_CREATED, {
        entity_id: result.id,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        data: greenworms_waste_itemData
      })

      setShowAddModal(false)
      console.log('✅ Waste Item created successfully')
    } catch (error) {
      console.error('❌ Error adding greenworms_waste_item:', error)
    }
  }

  const handleEditWasteItem = async (greenworms_waste_itemData: any) => {
    if (!currentWasteItem) return
    
    try {
      await greenworms_waste_itemData.update(currentWasteItem.entity_id!, {
        entity_name: greenworms_waste_itemData.entity_name
      }, greenworms_waste_itemData)

      // Emit update event
      await greenworms_waste_itemData.emitEvent(GREENWORMS_WASTE_ITEM_SMART_CODES.EVENT_UPDATED, {
        entity_id: currentWasteItem.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        changes: greenworms_waste_itemData
      })

      setShowEditModal(false)
      setCurrentWasteItem(null)
      console.log('✅ Waste Item updated successfully')
    } catch (error) {
      console.error('❌ Error updating greenworms_waste_item:', error)
    }
  }

  const handleDeleteWasteItem = async () => {
    if (!greenworms_waste_itemToDelete) return
    
    try {
      await greenworms_waste_itemData.delete(greenworms_waste_itemToDelete.entity_id!)

      // Emit deletion event
      await greenworms_waste_itemData.emitEvent(GREENWORMS_WASTE_ITEM_SMART_CODES.EVENT_DELETED, {
        entity_id: greenworms_waste_itemToDelete.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        entity_name: greenworms_waste_itemToDelete.entity_name
      })

      setShowDeleteModal(false)
      setWasteItemToDelete(null)
      console.log('✅ Waste Item deleted successfully')
    } catch (error) {
      console.error('❌ Error deleting greenworms_waste_item:', error)
    }
  }

  
  

  // Enterprise security checks
  if (!isAuthenticated) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
        <p>Please log in to access Waste Items.</p>
      </div>
    )
  }

  if (greenworms_waste_itemData.contextLoading) {
    return (
      <div className="p-4 text-center">
        <div className="animate-spin h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
        <p>Loading Waste Items...</p>
      </div>
    )
  }

  if (!currentOrganization) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-yellow-500 mx-auto mb-2" />
        <p>No organization context found. Please select an organization.</p>
      </div>
    )
  }

  return (
    <MobilePageLayout
      title="Waste Items"
      subtitle={`${greenworms_waste_items.length} total greenworms_waste_items`}
      primaryColor="#65a30d"
      accentColor="#4d7c0f"
      showBackButton={false}
    >
      {/* Enterprise KPI Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {kpis.map((kpi, index) => (
          <MobileCard key={index} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 font-medium">{kpi.title}</p>
                <p className="text-2xl font-bold" style={{ color: '#65a30d' }}>{kpi.value}</p>
                <p className={`text-xs font-medium ${kpi.trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                  {kpi.change}
                </p>
              </div>
              <kpi.icon className="h-8 w-8 text-gray-400" />
            </div>
          </MobileCard>
        ))}
      </div>

      {/* Enhanced Filters */}
      <MobileFilters
        fields={filterFields}
        values={filters}
        onChange={setFilters}
        className="mb-6"
      />

      {/* Enterprise Data Table */}
      <MobileDataTable
        data={greenworms_waste_items}
        columns={columns}
        selectedRows={selectedWasteItems}
        onRowSelect={setSelectedWasteItems}
        onRowClick={(greenworms_waste_item) => {
          setCurrentWasteItem(greenworms_waste_item)
          setShowEditModal(true)
        }}
        showBulkActions={selectedWasteItems.length > 0}
        bulkActions={[
          {
            label: 'Delete Selected',
            action: async () => {
              // Bulk delete with events
              for (const id of selectedWasteItems) {
                await greenworms_waste_itemData.delete(id.toString())
              }
              setSelectedWasteItems([])
            },
            variant: 'destructive'
          }
        ]}
        mobileCardRender={(greenworms_waste_item) => (
          <MobileCard key={greenworms_waste_item.id} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-lg">{greenworms_waste_item.entity_name}</h3>
                
                
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setCurrentWasteItem(greenworms_waste_item)
                    setShowEditModal(true)
                  }}
                  className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                >
                  <Edit className="h-4 w-4" />
                </button>
                <button
                  onClick={() => {
                    setWasteItemToDelete(greenworms_waste_item)
                    setShowDeleteModal(true)
                  }}
                  className="p-1 text-red-600 hover:bg-red-50 rounded"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            {/* Dynamic fields display */}
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Waste Code:</span>{' '}
              {greenworms_waste_item.waste_code || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Category:</span>{' '}
              {greenworms_waste_item.category || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Uom:</span>{' '}
              {greenworms_waste_item.uom || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Hazardous:</span>{' '}
              {greenworms_waste_item.hazardous || 'N/A'}
            </div>
            
            <div className="text-xs text-gray-400 mt-2 pt-2 border-t">
              Created: {greenworms_waste_item.created_at ? new Date(greenworms_waste_item.created_at).toLocaleDateString() : 'N/A'}
            </div>
          </MobileCard>
        )}
      />

      {/* Floating Action Button */}
      <button
        onClick={() => setShowAddModal(true)}
        className="fixed bottom-6 right-6 text-white rounded-full p-4 shadow-lg transition-colors z-50 hover:shadow-xl"
        style={{ backgroundColor: '#65a30d' }}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Enterprise Modals */}
      {showAddModal && (
        <WasteItemModal
          title="Add New Waste Item"
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          onSave={handleAddWasteItem}
          dynamicFields={greenworms_waste_itemData.dynamicFieldsConfig || []}
          businessRules={{"hazardous_tracking":true,"audit_trail":true}}
        />
      )}

      {showEditModal && currentWasteItem && (
        <WasteItemModal
          title="Edit Waste Item"
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false)
            setCurrentWasteItem(null)
          }}
          onSave={handleEditWasteItem}
          initialData={currentWasteItem}
          dynamicFields={greenworms_waste_itemData.dynamicFieldsConfig || []}
          businessRules={{"hazardous_tracking":true,"audit_trail":true}}
        />
      )}

      {showDeleteModal && greenworms_waste_itemToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex items-center mb-4">
              <AlertCircle className="h-6 w-6 text-red-500 mr-2" />
              <h3 className="text-lg font-semibold">Delete Waste Item</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete "{greenworms_waste_itemToDelete.entity_name}"? This action cannot be undone.
            </p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => {
                  setShowDeleteModal(false)
                  setWasteItemToDelete(null)
                }}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteWasteItem}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </MobilePageLayout>
  )
}

/**
 * Enterprise Waste Item Modal Component
 * Enhanced with business rules and validation
 */
interface WasteItemModalProps {
  title: string
  isOpen: boolean
  onClose: () => void
  onSave: (data: any) => void
  initialData?: WasteItem
  dynamicFields: any[]
  businessRules: any
}

function WasteItemModal({ 
  title, 
  isOpen, 
  onClose, 
  onSave, 
  initialData, 
  dynamicFields,
  businessRules 
}: WasteItemModalProps) {
  const [formData, setFormData] = useState(() => {
    const initial: any = { 
      entity_name: initialData?.entity_name || '' 
    }
    
    dynamicFields.forEach(field => {
      initial[field.name] = initialData?.[field.name as keyof WasteItem] || (field.type === 'number' ? 0 : '')
    })
    
    return initial
  })

  const [errors, setErrors] = useState<Record<string, string>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  const validateForm = () => {
    const newErrors: Record<string, string> = {}
    
    // Validate required fields
    dynamicFields.forEach(field => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label || field.name} is required`
      }
    })
    
    // Entity name validation
    if (!formData.entity_name?.trim()) {
      newErrors.entity_name = 'Waste Item name is required'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!validateForm()) return
    
    setIsSubmitting(true)
    try {
      await onSave(formData)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-xl font-semibold">{title}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Entity Name Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Waste Item Name *
              </label>
              <input
                type="text"
                value={formData.entity_name}
                onChange={(e) => setFormData({ ...formData, entity_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.entity_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.entity_name && (
                <p className="mt-1 text-sm text-red-600">{errors.entity_name}</p>
              )}
            </div>

            {/* Dynamic Fields with Enhanced Validation */}
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Waste Code 
              </label>
              <input
                type="text"
                value={formData.waste_code}
                onChange={(e) => setFormData({ ...formData, waste_code: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.waste_code ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.waste_code && (
                <p className="mt-1 text-sm text-red-600">{errors.waste_code}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Category 
              </label>
              <input
                type="text"
                value={formData.category}
                onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.category ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.category && (
                <p className="mt-1 text-sm text-red-600">{errors.category}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Uom 
              </label>
              <input
                type="text"
                value={formData.uom}
                onChange={(e) => setFormData({ ...formData, uom: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.uom ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.uom && (
                <p className="mt-1 text-sm text-red-600">{errors.uom}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Hazardous 
              </label>
              <input
                type="text"
                value={formData.hazardous}
                onChange={(e) => setFormData({ ...formData, hazardous: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.hazardous ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.hazardous && (
                <p className="mt-1 text-sm text-red-600">{errors.hazardous}</p>
              )}
            </div>

            {/* Business Rules Info */}
            

            {/* Form Actions */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-white rounded-md hover:opacity-90 flex items-center disabled:opacity-50"
                style={{ backgroundColor: '#65a30d' }}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}