'use client'

/**
 * Segments CRUD Page
 * Generated by HERA Enterprise Generator
 * 
 * Module: MCA
 * Entity: SEGMENT
 * Smart Code: HERA.CRM.MCA.ENTITY.SEGMENT.V1
 * Description: Dynamic audience definitions with DSL filters
 */

import React, { useState, useCallback, useEffect } from 'react'
import { MobilePageLayout } from '@/components/mobile/MobilePageLayout'
import { MobileFilters, type FilterField } from '@/components/mobile/MobileFilters'
import { MobileDataTable, type TableColumn, type TableRecord } from '@/components/mobile/MobileDataTable'
import { MobileCard } from '@/components/mobile/MobileCard'
import { MobileChart } from '@/components/mobile/MobileCharts'
import { useHERAAuth } from '@/components/auth/HERAAuthProvider'
import { useUniversalEntity } from '@/hooks/useUniversalEntity'
import { 
  AlertCircle,
  CheckCircle,
  Clock,
  Download,
  Edit,
  Eye,
  Filter,
  MoreVertical,
  Plus,
  Save,
  Search,
  Trash2,
  TrendingUp,
  Upload,
  Users,
  X
} from 'lucide-react'

/**
 * Segment Entity Interface
 * Extends TableRecord for HERA compliance
 */
interface Segment extends TableRecord {
  id: string
  entity_id?: string
  entity_name: string
  smart_code: string
  status?: string
  
  // Dynamic fields (stored in core_dynamic_data)
  name?: string
  description?: string
  dsl_filter?: string
  audience_count?: string
  last_compiled?: string
  tags?: string
  
  // System fields
  created_at?: string
  updated_at?: string
  created_by?: string
  updated_by?: string
  
  // Business rule fields
  
  
}

/**
 * HERA Segment Smart Codes
 * Auto-generated from preset configuration
 */
const SEGMENT_SMART_CODES = {
  ENTITY: 'HERA.CRM.MCA.ENTITY.SEGMENT.V1',
  FIELD_NAME: 'HERA.CRM.MCA.DYN.SEGMENT.V1.NAME.V1',
  FIELD_DESCRIPTION: 'HERA.CRM.MCA.DYN.SEGMENT.V1.DESCRIPTION.V1',
  FIELD_DSL_FILTER: 'HERA.CRM.MCA.DYN.SEGMENT.V1.DSL_FILTER.V1',
  FIELD_AUDIENCE_COUNT: 'HERA.CRM.MCA.DYN.SEGMENT.V1.AUDIENCE_COUNT.V1',
  FIELD_LAST_COMPILED: 'HERA.CRM.MCA.DYN.SEGMENT.V1.LAST_COMPILED.V1',
  FIELD_TAGS: 'HERA.CRM.MCA.DYN.SEGMENT.V1.TAGS.V1',
  
  // Event smart codes for audit trail
  EVENT_CREATED: 'HERA.CRM.MCA.EVENT.SEGMENT.V1.CREATED.V1',
  EVENT_UPDATED: 'HERA.CRM.MCA.EVENT.SEGMENT.V1.UPDATED.V1',
  EVENT_DELETED: 'HERA.CRM.MCA.EVENT.SEGMENT.V1.DELETED.V1'
} as const

/**
 * Segments Main Page Component
 * Enterprise-grade CRUD with quality gates and business rules
 */
export default function SegmentsPage() {
  const { currentOrganization, isAuthenticated, user } = useHERAAuth()
  const [selectedSegments, setSelectedSegments] = useState<(string | number)[]>([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)
  const [showDeleteModal, setShowDeleteModal] = useState(false)
  const [currentSegment, setCurrentSegment] = useState<Segment | null>(null)
  const [segmentToDelete, setSegmentToDelete] = useState<Segment | null>(null)
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    // Dynamic filter fields
    name: '',
    description: '',
    dsl_filter: ''
  })

  // HERA Universal Entity Integration
  const segmentData = useUniversalEntity({
    entity_type: 'SEGMENT',
    organizationId: currentOrganization?.id,
    filters: {
      include_dynamic: true,
      include_relationships: undefined,
      status: 'active'
    },
    dynamicFields: [
      { name: 'name', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.SEGMENT.V1.NAME.V1', required: false },
      { name: 'description', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.SEGMENT.V1.DESCRIPTION.V1', required: false },
      { name: 'dsl_filter', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.SEGMENT.V1.DSL_FILTER.V1', required: false },
      { name: 'audience_count', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.SEGMENT.V1.AUDIENCE_COUNT.V1', required: false },
      { name: 'last_compiled', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.SEGMENT.V1.LAST_COMPILED.V1', required: false },
      { name: 'tags', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.SEGMENT.V1.TAGS.V1', required: false }
    ]
  })

  // Transform entities with business rule extensions
  const segments: Segment[] = segmentData.entities?.map((entity: any) => {
    return {
      id: entity.id,
      entity_id: entity.id,
      entity_name: entity.entity_name || '',
      smart_code: entity.smart_code || '',
      status: entity.status || 'active',
      
      // Map dynamic fields with type safety
      name: entity.dynamic_data?.find((d: any) => d.field_name === 'name')?.field_value_text || '',
      description: entity.dynamic_data?.find((d: any) => d.field_name === 'description')?.field_value_text || '',
      dsl_filter: entity.dynamic_data?.find((d: any) => d.field_name === 'dsl_filter')?.field_value_text || '',
      audience_count: entity.dynamic_data?.find((d: any) => d.field_name === 'audience_count')?.field_value_text || '',
      last_compiled: entity.dynamic_data?.find((d: any) => d.field_name === 'last_compiled')?.field_value_text || '',
      tags: entity.dynamic_data?.find((d: any) => d.field_name === 'tags')?.field_value_text || '',
      
      // System fields
      created_at: entity.created_at,
      updated_at: entity.updated_at,
      created_by: entity.created_by,
      updated_by: entity.updated_by
      
      
    }
  }) || []

  // Enhanced KPI calculations with preset metrics
  const kpis = [
    {
      title: 'Total Segments',
      value: segments.length.toString(),
      change: '+5.2%',
      trend: 'up' as const,
      icon: Users
    },
    {
      title: 'Active Segments',
      value: segments.filter(item => item.status === 'active').length.toString(),
      change: '+2.1%',
      trend: 'up' as const,
      icon: CheckCircle
    },
    {
      title: 'This Month',
      value: segments.filter(item => {
        if (!item.created_at) return false
        const created = new Date(item.created_at)
        const now = new Date()
        return created.getMonth() === now.getMonth() && created.getFullYear() === now.getFullYear()
      }).length.toString(),
      change: '+8.3%',
      trend: 'up' as const,
      icon: TrendingUp
    }
  ]

  // Enhanced table columns with business rule columns
  const columns: TableColumn[] = [
    { key: 'entity_name', label: 'Segment Name', sortable: true },
    { key: 'name', label: 'Name', sortable: true },
    { key: 'description', label: 'Description', sortable: true },
    { key: 'dsl_filter', label: 'Dsl filter', sortable: true },
    { key: 'audience_count', label: 'Audience count', sortable: true },
    { key: 'last_compiled', label: 'Last compiled', sortable: true },
    { key: 'created_at', label: 'Created', sortable: true },
    { key: 'actions', label: 'Actions', sortable: false }
  ]

  // Enhanced filter fields with business rule filters
  const filterFields: FilterField[] = [
    { key: 'search', label: 'Search Segments', type: 'search' },
    { key: 'status', label: 'Status', type: 'select', options: [
      { value: '', label: 'All Status' },
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'Inactive' }
    ]},
    { key: 'name', label: 'Name', type: 'select', options: [
        { value: '', label: 'All Names' },
        ...Array.from(new Set(segments.map(item => item.name).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]},
    { key: 'description', label: 'Description', type: 'select', options: [
        { value: '', label: 'All Descriptions' },
        ...Array.from(new Set(segments.map(item => item.description).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]}
  ]

  // Enterprise CRUD Operations with Events
  const handleAddSegment = async (segmentData: any) => {
    try {
      const result = await segmentData.create({
        entity_type: 'SEGMENT',
        entity_name: segmentData.entity_name,
        smart_code: SEGMENT_SMART_CODES.ENTITY,
        organization_id: currentOrganization?.id
      }, segmentData)

      // Emit creation event for audit trail
      await segmentData.emitEvent(SEGMENT_SMART_CODES.EVENT_CREATED, {
        entity_id: result.id,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        data: segmentData
      })

      setShowAddModal(false)
      console.log('✅ Segment created successfully')
    } catch (error) {
      console.error('❌ Error adding segment:', error)
    }
  }

  const handleEditSegment = async (segmentData: any) => {
    if (!currentSegment) return
    
    try {
      await segmentData.update(currentSegment.entity_id!, {
        entity_name: segmentData.entity_name
      }, segmentData)

      // Emit update event
      await segmentData.emitEvent(SEGMENT_SMART_CODES.EVENT_UPDATED, {
        entity_id: currentSegment.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        changes: segmentData
      })

      setShowEditModal(false)
      setCurrentSegment(null)
      console.log('✅ Segment updated successfully')
    } catch (error) {
      console.error('❌ Error updating segment:', error)
    }
  }

  const handleDeleteSegment = async () => {
    if (!segmentToDelete) return
    
    try {
      await segmentData.delete(segmentToDelete.entity_id!)

      // Emit deletion event
      await segmentData.emitEvent(SEGMENT_SMART_CODES.EVENT_DELETED, {
        entity_id: segmentToDelete.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        entity_name: segmentToDelete.entity_name
      })

      setShowDeleteModal(false)
      setSegmentToDelete(null)
      console.log('✅ Segment deleted successfully')
    } catch (error) {
      console.error('❌ Error deleting segment:', error)
    }
  }

  
  

  // Enterprise security checks
  if (!isAuthenticated) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
        <p>Please log in to access Segments.</p>
      </div>
    )
  }

  if (segmentData.contextLoading) {
    return (
      <div className="p-4 text-center">
        <div className="animate-spin h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
        <p>Loading Segments...</p>
      </div>
    )
  }

  if (!currentOrganization) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-yellow-500 mx-auto mb-2" />
        <p>No organization context found. Please select an organization.</p>
      </div>
    )
  }

  return (
    <MobilePageLayout
      title="Segments"
      subtitle={`${segments.length} total segments`}
      primaryColor="#d83b01"
      accentColor="#a62d01"
      showBackButton={false}
    >
      {/* Enterprise KPI Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {kpis.map((kpi, index) => (
          <MobileCard key={index} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 font-medium">{kpi.title}</p>
                <p className="text-2xl font-bold" style={{ color: '#d83b01' }}>{kpi.value}</p>
                <p className={`text-xs font-medium ${kpi.trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                  {kpi.change}
                </p>
              </div>
              <kpi.icon className="h-8 w-8 text-gray-400" />
            </div>
          </MobileCard>
        ))}
      </div>

      {/* Enhanced Filters */}
      <MobileFilters
        fields={filterFields}
        values={filters}
        onChange={setFilters}
        className="mb-6"
      />

      {/* Enterprise Data Table */}
      <MobileDataTable
        data={segments}
        columns={columns}
        selectedRows={selectedSegments}
        onRowSelect={setSelectedSegments}
        onRowClick={(segment) => {
          setCurrentSegment(segment)
          setShowEditModal(true)
        }}
        showBulkActions={selectedSegments.length > 0}
        bulkActions={[
          {
            label: 'Delete Selected',
            action: async () => {
              // Bulk delete with events
              for (const id of selectedSegments) {
                await segmentData.delete(id.toString())
              }
              setSelectedSegments([])
            },
            variant: 'destructive'
          }
        ]}
        mobileCardRender={(segment) => (
          <MobileCard key={segment.id} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-lg">{segment.entity_name}</h3>
                
                
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setCurrentSegment(segment)
                    setShowEditModal(true)
                  }}
                  className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                >
                  <Edit className="h-4 w-4" />
                </button>
                <button
                  onClick={() => {
                    setSegmentToDelete(segment)
                    setShowDeleteModal(true)
                  }}
                  className="p-1 text-red-600 hover:bg-red-50 rounded"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            {/* Dynamic fields display */}
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Name:</span>{' '}
              {segment.name || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Description:</span>{' '}
              {segment.description || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Dsl Filter:</span>{' '}
              {segment.dsl_filter || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Audience Count:</span>{' '}
              {segment.audience_count || 'N/A'}
            </div>
            
            <div className="text-xs text-gray-400 mt-2 pt-2 border-t">
              Created: {segment.created_at ? new Date(segment.created_at).toLocaleDateString() : 'N/A'}
            </div>
          </MobileCard>
        )}
      />

      {/* Floating Action Button */}
      <button
        onClick={() => setShowAddModal(true)}
        className="fixed bottom-6 right-6 text-white rounded-full p-4 shadow-lg transition-colors z-50 hover:shadow-xl"
        style={{ backgroundColor: '#d83b01' }}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Enterprise Modals */}
      {showAddModal && (
        <SegmentModal
          title="Add New Segment"
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          onSave={handleAddSegment}
          dynamicFields={segmentData.dynamicFieldsConfig || []}
          businessRules={{"dynamic_compilation":true,"audit_trail":true,"performance_monitoring":true}}
        />
      )}

      {showEditModal && currentSegment && (
        <SegmentModal
          title="Edit Segment"
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false)
            setCurrentSegment(null)
          }}
          onSave={handleEditSegment}
          initialData={currentSegment}
          dynamicFields={segmentData.dynamicFieldsConfig || []}
          businessRules={{"dynamic_compilation":true,"audit_trail":true,"performance_monitoring":true}}
        />
      )}

      {showDeleteModal && segmentToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex items-center mb-4">
              <AlertCircle className="h-6 w-6 text-red-500 mr-2" />
              <h3 className="text-lg font-semibold">Delete Segment</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete "{segmentToDelete.entity_name}"? This action cannot be undone.
            </p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => {
                  setShowDeleteModal(false)
                  setSegmentToDelete(null)
                }}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteSegment}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </MobilePageLayout>
  )
}

/**
 * Enterprise Segment Modal Component
 * Enhanced with business rules and validation
 */
interface SegmentModalProps {
  title: string
  isOpen: boolean
  onClose: () => void
  onSave: (data: any) => void
  initialData?: Segment
  dynamicFields: any[]
  businessRules: any
}

function SegmentModal({ 
  title, 
  isOpen, 
  onClose, 
  onSave, 
  initialData, 
  dynamicFields,
  businessRules 
}: SegmentModalProps) {
  const [formData, setFormData] = useState(() => {
    const initial: any = { 
      entity_name: initialData?.entity_name || '' 
    }
    
    dynamicFields.forEach(field => {
      initial[field.name] = initialData?.[field.name as keyof Segment] || (field.type === 'number' ? 0 : '')
    })
    
    return initial
  })

  const [errors, setErrors] = useState<Record<string, string>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  const validateForm = () => {
    const newErrors: Record<string, string> = {}
    
    // Validate required fields
    dynamicFields.forEach(field => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label || field.name} is required`
      }
    })
    
    // Entity name validation
    if (!formData.entity_name?.trim()) {
      newErrors.entity_name = 'Segment name is required'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!validateForm()) return
    
    setIsSubmitting(true)
    try {
      await onSave(formData)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-xl font-semibold">{title}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Entity Name Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Segment Name *
              </label>
              <input
                type="text"
                value={formData.entity_name}
                onChange={(e) => setFormData({ ...formData, entity_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.entity_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.entity_name && (
                <p className="mt-1 text-sm text-red-600">{errors.entity_name}</p>
              )}
            </div>

            {/* Dynamic Fields with Enhanced Validation */}
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Name 
              </label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.name && (
                <p className="mt-1 text-sm text-red-600">{errors.name}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Description 
              </label>
              <input
                type="text"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.description ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.description && (
                <p className="mt-1 text-sm text-red-600">{errors.description}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Dsl Filter 
              </label>
              <input
                type="text"
                value={formData.dsl_filter}
                onChange={(e) => setFormData({ ...formData, dsl_filter: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.dsl_filter ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.dsl_filter && (
                <p className="mt-1 text-sm text-red-600">{errors.dsl_filter}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Audience Count 
              </label>
              <input
                type="text"
                value={formData.audience_count}
                onChange={(e) => setFormData({ ...formData, audience_count: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.audience_count ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.audience_count && (
                <p className="mt-1 text-sm text-red-600">{errors.audience_count}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Last Compiled 
              </label>
              <input
                type="text"
                value={formData.last_compiled}
                onChange={(e) => setFormData({ ...formData, last_compiled: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.last_compiled ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.last_compiled && (
                <p className="mt-1 text-sm text-red-600">{errors.last_compiled}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Tags 
              </label>
              <input
                type="text"
                value={formData.tags}
                onChange={(e) => setFormData({ ...formData, tags: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.tags ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.tags && (
                <p className="mt-1 text-sm text-red-600">{errors.tags}</p>
              )}
            </div>

            {/* Business Rules Info */}
            

            {/* Form Actions */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-white rounded-md hover:opacity-90 flex items-center disabled:opacity-50"
                style={{ backgroundColor: '#d83b01' }}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}