'use client'

/**
 * Templates CRUD Page
 * Generated by HERA Enterprise Generator
 * 
 * Module: MCA
 * Entity: TEMPLATE
 * Smart Code: HERA.CRM.MCA.ENTITY.TEMPLATE.V1
 * Description: Omni-channel templates with WCAG 2.1 AA compliance
 */

import React, { useState, useCallback, useEffect } from 'react'
import { MobilePageLayout } from '@/components/mobile/MobilePageLayout'
import { MobileFilters, type FilterField } from '@/components/mobile/MobileFilters'
import { MobileDataTable, type TableColumn, type TableRecord } from '@/components/mobile/MobileDataTable'
import { MobileCard } from '@/components/mobile/MobileCard'
import { MobileChart } from '@/components/mobile/MobileCharts'
import { useHERAAuth } from '@/components/auth/HERAAuthProvider'
import { useUniversalEntity } from '@/hooks/useUniversalEntity'
import { 
  AlertCircle,
  CheckCircle,
  Clock,
  Download,
  Edit,
  Eye,
  FileText,
  Filter,
  MoreVertical,
  Plus,
  Save,
  Search,
  Trash2,
  TrendingUp,
  Upload,
  X
} from 'lucide-react'

/**
 * Template Entity Interface
 * Extends TableRecord for HERA compliance
 */
interface Template extends TableRecord {
  id: string
  entity_id?: string
  entity_name: string
  smart_code: string
  status?: string
  
  // Dynamic fields (stored in core_dynamic_data)
  name?: string
  channel_type?: string
  subject?: string
  blocks?: string
  variables?: string
  wcag_score?: string
  version?: string
  
  // System fields
  created_at?: string
  updated_at?: string
  created_by?: string
  updated_by?: string
  
  // Business rule fields
  
  
}

/**
 * HERA Template Smart Codes
 * Auto-generated from preset configuration
 */
const TEMPLATE_SMART_CODES = {
  ENTITY: 'HERA.CRM.MCA.ENTITY.TEMPLATE.V1',
  FIELD_NAME: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.NAME.V1',
  FIELD_CHANNEL_TYPE: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.CHANNEL_TYPE.V1',
  FIELD_SUBJECT: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.SUBJECT.V1',
  FIELD_BLOCKS: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.BLOCKS.V1',
  FIELD_VARIABLES: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.VARIABLES.V1',
  FIELD_WCAG_SCORE: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.WCAG_SCORE.V1',
  FIELD_VERSION: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.VERSION.V1',
  
  // Event smart codes for audit trail
  EVENT_CREATED: 'HERA.CRM.MCA.EVENT.TEMPLATE.V1.CREATED.V1',
  EVENT_UPDATED: 'HERA.CRM.MCA.EVENT.TEMPLATE.V1.UPDATED.V1',
  EVENT_DELETED: 'HERA.CRM.MCA.EVENT.TEMPLATE.V1.DELETED.V1'
} as const

/**
 * Templates Main Page Component
 * Enterprise-grade CRUD with quality gates and business rules
 */
export default function TemplatesPage() {
  const { currentOrganization, isAuthenticated, user } = useHERAAuth()
  const [selectedTemplates, setSelectedTemplates] = useState<(string | number)[]>([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)
  const [showDeleteModal, setShowDeleteModal] = useState(false)
  const [currentTemplate, setCurrentTemplate] = useState<Template | null>(null)
  const [templateToDelete, setTemplateToDelete] = useState<Template | null>(null)
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    // Dynamic filter fields
    name: '',
    channel_type: '',
    subject: ''
  })

  // HERA Universal Entity Integration
  const templateData = useUniversalEntity({
    entity_type: 'TEMPLATE',
    organizationId: currentOrganization?.id,
    filters: {
      include_dynamic: true,
      include_relationships: undefined,
      status: 'active'
    },
    dynamicFields: [
      { name: 'name', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.NAME.V1', required: false },
      { name: 'channel_type', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.CHANNEL_TYPE.V1', required: false },
      { name: 'subject', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.SUBJECT.V1', required: false },
      { name: 'blocks', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.BLOCKS.V1', required: false },
      { name: 'variables', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.VARIABLES.V1', required: false },
      { name: 'wcag_score', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.WCAG_SCORE.V1', required: false },
      { name: 'version', type: 'text', smart_code: 'HERA.CRM.MCA.DYN.TEMPLATE.V1.VERSION.V1', required: false }
    ]
  })

  // Transform entities with business rule extensions
  const templates: Template[] = templateData.entities?.map((entity: any) => {
    return {
      id: entity.id,
      entity_id: entity.id,
      entity_name: entity.entity_name || '',
      smart_code: entity.smart_code || '',
      status: entity.status || 'active',
      
      // Map dynamic fields with type safety
      name: entity.dynamic_data?.find((d: any) => d.field_name === 'name')?.field_value_text || '',
      channel_type: entity.dynamic_data?.find((d: any) => d.field_name === 'channel_type')?.field_value_text || '',
      subject: entity.dynamic_data?.find((d: any) => d.field_name === 'subject')?.field_value_text || '',
      blocks: entity.dynamic_data?.find((d: any) => d.field_name === 'blocks')?.field_value_text || '',
      variables: entity.dynamic_data?.find((d: any) => d.field_name === 'variables')?.field_value_text || '',
      wcag_score: entity.dynamic_data?.find((d: any) => d.field_name === 'wcag_score')?.field_value_text || '',
      version: entity.dynamic_data?.find((d: any) => d.field_name === 'version')?.field_value_text || '',
      
      // System fields
      created_at: entity.created_at,
      updated_at: entity.updated_at,
      created_by: entity.created_by,
      updated_by: entity.updated_by
      
      
    }
  }) || []

  // Enhanced KPI calculations with preset metrics
  const kpis = [
    {
      title: 'Total Templates',
      value: templates.length.toString(),
      change: '+5.2%',
      trend: 'up' as const,
      icon: FileText
    },
    {
      title: 'Active Templates',
      value: templates.filter(item => item.status === 'active').length.toString(),
      change: '+2.1%',
      trend: 'up' as const,
      icon: CheckCircle
    },
    {
      title: 'This Month',
      value: templates.filter(item => {
        if (!item.created_at) return false
        const created = new Date(item.created_at)
        const now = new Date()
        return created.getMonth() === now.getMonth() && created.getFullYear() === now.getFullYear()
      }).length.toString(),
      change: '+8.3%',
      trend: 'up' as const,
      icon: TrendingUp
    }
  ]

  // Enhanced table columns with business rule columns
  const columns: TableColumn[] = [
    { key: 'entity_name', label: 'Template Name', sortable: true },
    { key: 'name', label: 'Name', sortable: true },
    { key: 'channel_type', label: 'Channel type', sortable: true },
    { key: 'subject', label: 'Subject', sortable: true },
    { key: 'blocks', label: 'Blocks', sortable: true },
    { key: 'variables', label: 'Variables', sortable: true },
    { key: 'created_at', label: 'Created', sortable: true },
    { key: 'actions', label: 'Actions', sortable: false }
  ]

  // Enhanced filter fields with business rule filters
  const filterFields: FilterField[] = [
    { key: 'search', label: 'Search Templates', type: 'search' },
    { key: 'status', label: 'Status', type: 'select', options: [
      { value: '', label: 'All Status' },
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'Inactive' }
    ]},
    { key: 'name', label: 'Name', type: 'select', options: [
        { value: '', label: 'All Names' },
        ...Array.from(new Set(templates.map(item => item.name).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]},
    { key: 'channel_type', label: 'Channel Type', type: 'select', options: [
        { value: '', label: 'All Channel Types' },
        ...Array.from(new Set(templates.map(item => item.channel_type).filter(Boolean))).map(val => ({ value: val!, label: val! }))
      ]}
  ]

  // Enterprise CRUD Operations with Events
  const handleAddTemplate = async (templateData: any) => {
    try {
      const result = await templateData.create({
        entity_type: 'TEMPLATE',
        entity_name: templateData.entity_name,
        smart_code: TEMPLATE_SMART_CODES.ENTITY,
        organization_id: currentOrganization?.id
      }, templateData)

      // Emit creation event for audit trail
      await templateData.emitEvent(TEMPLATE_SMART_CODES.EVENT_CREATED, {
        entity_id: result.id,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        data: templateData
      })

      setShowAddModal(false)
      console.log('✅ Template created successfully')
    } catch (error) {
      console.error('❌ Error adding template:', error)
    }
  }

  const handleEditTemplate = async (templateData: any) => {
    if (!currentTemplate) return
    
    try {
      await templateData.update(currentTemplate.entity_id!, {
        entity_name: templateData.entity_name
      }, templateData)

      // Emit update event
      await templateData.emitEvent(TEMPLATE_SMART_CODES.EVENT_UPDATED, {
        entity_id: currentTemplate.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        changes: templateData
      })

      setShowEditModal(false)
      setCurrentTemplate(null)
      console.log('✅ Template updated successfully')
    } catch (error) {
      console.error('❌ Error updating template:', error)
    }
  }

  const handleDeleteTemplate = async () => {
    if (!templateToDelete) return
    
    try {
      await templateData.delete(templateToDelete.entity_id!)

      // Emit deletion event
      await templateData.emitEvent(TEMPLATE_SMART_CODES.EVENT_DELETED, {
        entity_id: templateToDelete.entity_id!,
        user_id: user?.id,
        timestamp: new Date().toISOString(),
        entity_name: templateToDelete.entity_name
      })

      setShowDeleteModal(false)
      setTemplateToDelete(null)
      console.log('✅ Template deleted successfully')
    } catch (error) {
      console.error('❌ Error deleting template:', error)
    }
  }

  
  

  // Enterprise security checks
  if (!isAuthenticated) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
        <p>Please log in to access Templates.</p>
      </div>
    )
  }

  if (templateData.contextLoading) {
    return (
      <div className="p-4 text-center">
        <div className="animate-spin h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-2"></div>
        <p>Loading Templates...</p>
      </div>
    )
  }

  if (!currentOrganization) {
    return (
      <div className="p-4 text-center">
        <AlertCircle className="h-8 w-8 text-yellow-500 mx-auto mb-2" />
        <p>No organization context found. Please select an organization.</p>
      </div>
    )
  }

  return (
    <MobilePageLayout
      title="Templates"
      subtitle={`${templates.length} total templates`}
      primaryColor="#0078d4"
      accentColor="#005a9e"
      showBackButton={false}
    >
      {/* Enterprise KPI Dashboard */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {kpis.map((kpi, index) => (
          <MobileCard key={index} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 font-medium">{kpi.title}</p>
                <p className="text-2xl font-bold" style={{ color: '#0078d4' }}>{kpi.value}</p>
                <p className={`text-xs font-medium ${kpi.trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                  {kpi.change}
                </p>
              </div>
              <kpi.icon className="h-8 w-8 text-gray-400" />
            </div>
          </MobileCard>
        ))}
      </div>

      {/* Enhanced Filters */}
      <MobileFilters
        fields={filterFields}
        values={filters}
        onChange={setFilters}
        className="mb-6"
      />

      {/* Enterprise Data Table */}
      <MobileDataTable
        data={templates}
        columns={columns}
        selectedRows={selectedTemplates}
        onRowSelect={setSelectedTemplates}
        onRowClick={(template) => {
          setCurrentTemplate(template)
          setShowEditModal(true)
        }}
        showBulkActions={selectedTemplates.length > 0}
        bulkActions={[
          {
            label: 'Delete Selected',
            action: async () => {
              // Bulk delete with events
              for (const id of selectedTemplates) {
                await templateData.delete(id.toString())
              }
              setSelectedTemplates([])
            },
            variant: 'destructive'
          }
        ]}
        mobileCardRender={(template) => (
          <MobileCard key={template.id} className="p-4 hover:shadow-md transition-shadow">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-lg">{template.entity_name}</h3>
                
                
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setCurrentTemplate(template)
                    setShowEditModal(true)
                  }}
                  className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                >
                  <Edit className="h-4 w-4" />
                </button>
                <button
                  onClick={() => {
                    setTemplateToDelete(template)
                    setShowDeleteModal(true)
                  }}
                  className="p-1 text-red-600 hover:bg-red-50 rounded"
                >
                  <Trash2 className="h-4 w-4" />
                </button>
              </div>
            </div>
            
            {/* Dynamic fields display */}
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Name:</span>{' '}
              {template.name || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Channel Type:</span>{' '}
              {template.channel_type || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Subject:</span>{' '}
              {template.subject || 'N/A'}
            </div>
            <div className="text-sm text-gray-600 mb-1">
              <span className="font-medium">Blocks:</span>{' '}
              {template.blocks || 'N/A'}
            </div>
            
            <div className="text-xs text-gray-400 mt-2 pt-2 border-t">
              Created: {template.created_at ? new Date(template.created_at).toLocaleDateString() : 'N/A'}
            </div>
          </MobileCard>
        )}
      />

      {/* Floating Action Button */}
      <button
        onClick={() => setShowAddModal(true)}
        className="fixed bottom-6 right-6 text-white rounded-full p-4 shadow-lg transition-colors z-50 hover:shadow-xl"
        style={{ backgroundColor: '#0078d4' }}
      >
        <Plus className="h-6 w-6" />
      </button>

      {/* Enterprise Modals */}
      {showAddModal && (
        <TemplateModal
          title="Add New Template"
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          onSave={handleAddTemplate}
          dynamicFields={templateData.dynamicFieldsConfig || []}
          businessRules={{"version_control":true,"wcag_validation":true,"audit_trail":true}}
        />
      )}

      {showEditModal && currentTemplate && (
        <TemplateModal
          title="Edit Template"
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false)
            setCurrentTemplate(null)
          }}
          onSave={handleEditTemplate}
          initialData={currentTemplate}
          dynamicFields={templateData.dynamicFieldsConfig || []}
          businessRules={{"version_control":true,"wcag_validation":true,"audit_trail":true}}
        />
      )}

      {showDeleteModal && templateToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex items-center mb-4">
              <AlertCircle className="h-6 w-6 text-red-500 mr-2" />
              <h3 className="text-lg font-semibold">Delete Template</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to delete "{templateToDelete.entity_name}"? This action cannot be undone.
            </p>
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => {
                  setShowDeleteModal(false)
                  setTemplateToDelete(null)
                }}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteTemplate}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </MobilePageLayout>
  )
}

/**
 * Enterprise Template Modal Component
 * Enhanced with business rules and validation
 */
interface TemplateModalProps {
  title: string
  isOpen: boolean
  onClose: () => void
  onSave: (data: any) => void
  initialData?: Template
  dynamicFields: any[]
  businessRules: any
}

function TemplateModal({ 
  title, 
  isOpen, 
  onClose, 
  onSave, 
  initialData, 
  dynamicFields,
  businessRules 
}: TemplateModalProps) {
  const [formData, setFormData] = useState(() => {
    const initial: any = { 
      entity_name: initialData?.entity_name || '' 
    }
    
    dynamicFields.forEach(field => {
      initial[field.name] = initialData?.[field.name as keyof Template] || (field.type === 'number' ? 0 : '')
    })
    
    return initial
  })

  const [errors, setErrors] = useState<Record<string, string>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)

  const validateForm = () => {
    const newErrors: Record<string, string> = {}
    
    // Validate required fields
    dynamicFields.forEach(field => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label || field.name} is required`
      }
    })
    
    // Entity name validation
    if (!formData.entity_name?.trim()) {
      newErrors.entity_name = 'Template name is required'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!validateForm()) return
    
    setIsSubmitting(true)
    try {
      await onSave(formData)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-xl font-semibold">{title}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Entity Name Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Template Name *
              </label>
              <input
                type="text"
                value={formData.entity_name}
                onChange={(e) => setFormData({ ...formData, entity_name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.entity_name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                required
                disabled={isSubmitting}
              />
              {errors.entity_name && (
                <p className="mt-1 text-sm text-red-600">{errors.entity_name}</p>
              )}
            </div>

            {/* Dynamic Fields with Enhanced Validation */}
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Name 
              </label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.name ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.name && (
                <p className="mt-1 text-sm text-red-600">{errors.name}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Channel Type 
              </label>
              <input
                type="text"
                value={formData.channel_type}
                onChange={(e) => setFormData({ ...formData, channel_type: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.channel_type ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.channel_type && (
                <p className="mt-1 text-sm text-red-600">{errors.channel_type}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Subject 
              </label>
              <input
                type="text"
                value={formData.subject}
                onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.subject ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.subject && (
                <p className="mt-1 text-sm text-red-600">{errors.subject}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Blocks 
              </label>
              <input
                type="text"
                value={formData.blocks}
                onChange={(e) => setFormData({ ...formData, blocks: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.blocks ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.blocks && (
                <p className="mt-1 text-sm text-red-600">{errors.blocks}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Variables 
              </label>
              <input
                type="text"
                value={formData.variables}
                onChange={(e) => setFormData({ ...formData, variables: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.variables ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.variables && (
                <p className="mt-1 text-sm text-red-600">{errors.variables}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Wcag Score 
              </label>
              <input
                type="text"
                value={formData.wcag_score}
                onChange={(e) => setFormData({ ...formData, wcag_score: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.wcag_score ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.wcag_score && (
                <p className="mt-1 text-sm text-red-600">{errors.wcag_score}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Version 
              </label>
              <input
                type="text"
                value={formData.version}
                onChange={(e) => setFormData({ ...formData, version: e.target.value })}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${errors.version ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'}`}
                
                disabled={isSubmitting}
              />
              {errors.version && (
                <p className="mt-1 text-sm text-red-600">{errors.version}</p>
              )}
            </div>

            {/* Business Rules Info */}
            

            {/* Form Actions */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                disabled={isSubmitting}
                className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-white rounded-md hover:opacity-90 flex items-center disabled:opacity-50"
                style={{ backgroundColor: '#0078d4' }}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}