'use client'

import { useCallback, useState, useEffect } from 'react'
import { useHeraEntityListQuery, useHeraEntityQuery, useHeraCacheControl, useHeraQuery } from './useHeraQuery'
import { heraCommand } from '@/lib/hera/client'
import { useOrg } from './useOrg'
import { getFieldConfiguration, validateFormData, formDataToDynamicFields } from '@/lib/field-config'

export interface Leads {
  entity_id: string
  entity_name: string
  entity_type: 'LEAD'
  smart_code: string
  created_at: string
  updated_at: string
  created_by: string
  updated_by: string
  dynamic_fields: Record<string, {
    field_value_text?: string
    field_value_number?: number
    field_value_boolean?: boolean
    field_value_json?: any
    smart_code: string
  }>
  relationships?: Array<{
    relationship_type: string
    target_entity_id: string
    target_entity_name: string
  }>
}

export interface LeadsListParams {
  search?: string
  limit?: number
  offset?: number
  sort?: string
  order?: 'asc' | 'desc'
}

export interface CreateLeadsInput {
  entity_name: string
  dynamic_fields?: Record<string, any>
}

export interface UpdateLeadsInput {
  entity_name?: string
  dynamic_fields?: Record<string, any>
}

/**
 * Hook for listing leads with search and pagination
 * Uses RLS-scoped API endpoint for secure data access
 */
export function useLeadss(params: LeadsListParams = {}) {
  return useHeraEntityListQuery<Leads>(
    'leads',
    {
      search: params.search || '',
      limit: params.limit || 50,
      offset: params.offset || 0,
      sort: params.sort || 'entity_name',
      order: params.order || 'asc'
    },
    {
      requireAuth: true,
      requireOrg: true,
      requireMembership: true,
      revalidateOnFocus: false,
      dedupingInterval: 30000 // Cache for 30 seconds
    }
  )
}

/**
 * Hook for fetching a single leads by ID
 * Uses RLS-scoped API endpoint
 */
export function useLeads(leadsId?: string) {
  return useHeraEntityQuery<Leads>(
    'leads',
    leadsId,
    {
      requireAuth: true,
      requireOrg: true,
      requireMembership: true,
      revalidateOnFocus: false,
      dedupingInterval: 60000 // Cache for 1 minute
    }
  )
}

/**
 * Hook for leads mutation operations (CREATE/UPDATE/DELETE)
 * All operations go through API v2 command interface with actor stamping
 */
export function useLeadsMutations() {
  const { orgId } = useOrg()
  const { invalidateEntity } = useHeraCacheControl()

  const createLeads = useCallback(async (input: CreateLeadsInput) => {
    if (!orgId) {
      throw new Error('No organization context available')
    }

    // Get field configuration for validation
    const token = typeof window !== 'undefined' ? 
      (window as any).__HERA_JWT__ : ''
    const fieldConfig = await getFieldConfiguration('LEAD', orgId, token)
    
    // Validate form data
    const formData = {
      entity_name: input.entity_name,
      ...input.dynamic_fields
    }
    const errors = validateFormData(fieldConfig.fields, formData)
    
    if (Object.keys(errors).length > 0) {
      throw new Error(`Validation failed: ${Object.values(errors).join(', ')}`)
    }

    // Convert to dynamic fields format
    const dynamicFields = formDataToDynamicFields(fieldConfig.fields, formData)

    // Prepare API v2 command
    const payload = {
      op: "entities" as const,
      p_operation: "CREATE" as const,
      p_data: {
        entity_type: "LEAD",
        entity_name: input.entity_name,
        smart_code: "HERA.CRM.LEAD.v1",
        organization_id: orgId,
        dynamic_fields: dynamicFields
      }
    }

    const result = await heraCommand(payload, { orgId })

    // Invalidate cache
    await invalidateEntity('leads')

    return result
  }, [orgId, invalidateEntity])

  const updateLeads = useCallback(async (
    leadsId: string, 
    input: UpdateLeadsInput
  ) => {
    if (!orgId) {
      throw new Error('No organization context available')
    }

    // Get field configuration for validation
    const token = typeof window !== 'undefined' ? 
      (window as any).__HERA_JWT__ : ''
    const fieldConfig = await getFieldConfiguration('LEAD', orgId, token)
    
    // Validate form data
    const formData = {
      ...(input.entity_name && { entity_name: input.entity_name }),
      ...input.dynamic_fields
    }
    const errors = validateFormData(fieldConfig.fields, formData)
    
    if (Object.keys(errors).length > 0) {
      throw new Error(`Validation failed: ${Object.values(errors).join(', ')}`)
    }

    // Convert to dynamic fields format
    const dynamicFields = formDataToDynamicFields(fieldConfig.fields, formData)

    // Prepare API v2 command
    const payload = {
      op: "entities" as const,
      p_operation: "UPDATE" as const,
      p_data: {
        entity_id: leadsId,
        entity_type: "LEAD",
        smart_code: "HERA.CRM.LEAD.v1",
        organization_id: orgId,
        ...(input.entity_name && { entity_name: input.entity_name }),
        ...(dynamicFields.length > 0 && { dynamic_fields: dynamicFields })
      }
    }

    const result = await heraCommand(payload, { orgId })

    // Invalidate cache
    await invalidateEntity('leads', leadsId)
    await invalidateEntity('leads') // Also invalidate list

    return result
  }, [orgId, invalidateEntity])

  const deleteLeads = useCallback(async (leadsId: string) => {
    if (!orgId) {
      throw new Error('No organization context available')
    }

    // Prepare API v2 command
    const payload = {
      op: "entities" as const,
      p_operation: "DELETE" as const,
      p_data: {
        entity_id: leadsId,
        entity_type: "LEAD",
        smart_code: "HERA.CRM.LEAD.v1",
        organization_id: orgId
      }
    }

    const result = await heraCommand(payload, { orgId })

    // Invalidate cache
    await invalidateEntity('leads', leadsId)
    await invalidateEntity('leads') // Also invalidate list

    return result
  }, [orgId, invalidateEntity])

  return {
    createLeads,
    updateLeads,
    deleteLeads
  }
}

/**
 * Hook for leads field configuration
 * Returns org-specific field definitions for forms
 */
export function useLeadsFieldConfig() {
  const { orgId } = useOrg()
  const [fieldConfig, setFieldConfig] = useState(null)
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
    if (!orgId) return

    let isCancelled = false
    setIsLoading(true)
    setError(null)

    const loadConfig = async () => {
      try {
        const token = typeof window !== 'undefined' ? 
          (window as any).__HERA_JWT__ : ''
        const config = await getFieldConfiguration('LEAD', orgId, token)
        
        if (!isCancelled) {
          setFieldConfig(config)
        }
      } catch (err) {
        if (!isCancelled) {
          setError(err instanceof Error ? err.message : 'Failed to load field configuration')
        }
      } finally {
        if (!isCancelled) {
          setIsLoading(false)
        }
      }
    }

    loadConfig()

    return () => {
      isCancelled = true
    }
  }, [orgId])

  return { fieldConfig, isLoading, error }
}
